import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Card from '../components/Card';
import StatCard from '../components/StatCard';
import LineChart from '../components/LineChart';
import BarChart from '../components/BarChart';
import PieChart from '../components/PieChart';
import RevenueVsBaselineChart from '../components/RevenueVsBaselineChart';
import CustomerSegmentPie from '../components/CustomerSegmentPie';
import CustomerRetentionChart from '../components/CustomerRetentionChart';
import PriceDemandRevenueChart from '../components/PriceDemandRevenueChart';
import ProductPerformanceChart from '../components/ProductPerformanceChart';
import TimePricingChart from '../components/TimePricingChart';
import PriceSensitivityHeatmap from '../components/PriceSensitivityHeatmap';
import Button from '../components/Button';
import LoadingSpinner from '../components/LoadingSpinner';
import apiClient from '../api/apiClient';

const Dashboard: React.FC = () => {
  const [products, setProducts] = useState<any[]>([]);
  const [trainingStatus, setTrainingStatus] = useState<any>({
    isTraining: false,
    currentEpisode: 0,
    totalEpisodes: 100,
    startTime: null,
    endTime: null
  });
  const [trainingResults, setTrainingResults] = useState<any>({
    finalReward: 0,
    avgLast10: 0,
    improvementOverBaseline: 0,
    rewardHistory: [],
    baselineHistory: []
  });
  const [customerSegments, setCustomerSegments] = useState<any[]>([]);
  const [priceDemandData, setPriceDemandData] = useState<any[]>([]);
  const [timePricingData, setTimePricingData] = useState<any>(null);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [trainingEpisodes, setTrainingEpisodes] = useState<number>(100);

  // Fetch initial data
  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);

        // Fetch products
        const productsData = await apiClient.getProducts();
        setProducts(productsData);

        // Fetch customer segments
        const segmentsData = await apiClient.getCustomerSegments();
        setCustomerSegments(segmentsData);

        // Fetch price-demand data
        const priceDemandData = await apiClient.getPriceDemandData();
        setPriceDemandData(priceDemandData);

        // Fetch time-pricing data
        const timePricingData = await apiClient.getTimePricingData();
        setTimePricingData(timePricingData);

        // Fetch training status
        const statusData = await apiClient.getTrainingStatus();
        setTrainingStatus(statusData);

        // Fetch training results
        const resultsData = await apiClient.getTrainingResults();
        setTrainingResults(resultsData);

      } catch (err) {
        console.error('Error fetching dashboard data:', err);
        setError('Failed to load dashboard data. Please try refreshing the page.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();

    // Set up polling for training status and results
    const statusInterval = setInterval(async () => {
      try {
        const statusData = await apiClient.getTrainingStatus();
        setTrainingStatus(statusData);

        if (statusData.isTraining) {
          const resultsData = await apiClient.getTrainingResults();
          setTrainingResults(resultsData);
        }
      } catch (err) {
        console.error('Error polling training status:', err);
      }
    }, 2000);

    return () => {
      clearInterval(statusInterval);
    };
  }, []);

  const handleStartTraining = async () => {
    try {
      setError(null);
      await apiClient.startTraining({
        episodes: trainingEpisodes,
        useBaseline: true,
        baselineStrategy: 'combined'
      });

      // Immediately fetch updated status
      const statusData = await apiClient.getTrainingStatus();
      setTrainingStatus(statusData);
    } catch (err) {
      console.error('Error starting training:', err);
      setError('Failed to start training. Please try again.');
    }
  };

  const handleEpisodesChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = parseInt(e.target.value);
    if (!isNaN(value) && value > 0) {
      setTrainingEpisodes(value);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <div className="text-center">
          <LoadingSpinner size="large" />
          <p className="mt-4 text-gray-600">Loading dashboard data...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
        <Card className="max-w-lg w-full">
          <div className="p-6 text-center">
            <div className="w-16 h-16 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <svg xmlns="http://www.w3.org/2000/svg" className="h-8 w-8 text-red-500" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 8v4m0 4h.01M21 12a9 9 0 11-18 0 9 9 0 0118 0z" />
              </svg>
            </div>
            <h2 className="text-xl font-semibold text-gray-800 mb-2">Error Loading Dashboard</h2>
            <p className="text-gray-600 mb-4">{error}</p>
            <Button 
              onClick={() => window.location.reload()} 
              variant="primary"
            >
              Refresh Page
            </Button>
          </div>
        </Card>
      </div>
    );
  }

  // Calculate training progress percentage
  const trainingProgress = trainingStatus.isTraining && trainingStatus.totalEpisodes > 0
    ? Math.round((trainingStatus.currentEpisode / trainingStatus.totalEpisodes) * 100)
    : 0;

  return (
    <div className="min-h-screen bg-gray-100">
      <Header />

      <div className="container mx-auto px-4 py-6">
        {/* Training Controls */}
        <Card className="mb-6">
          <div className="p-4">
            <h2 className="text-xl font-semibold mb-4">AI Training Controls</h2>

            {trainingStatus.isTraining ? (
              <div>
                <div className="mb-4">
                  <div className="flex justify-between mb-1">
                    <span className="text-sm font-medium">Training Progress</span>
                    <span className="text-sm font-medium">{trainingProgress}%</span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2.5">
                    <div 
                      className="bg-blue-600 h-2.5 rounded-full transition-all duration-300" 
                      style={{ width: `${trainingProgress}%` }}
                    ></div>
                  </div>
                  <div className="mt-2 text-sm text-gray-600">
                    Episode {trainingStatus.currentEpisode} of {trainingStatus.totalEpisodes}
                  </div>
                </div>

                <div className="flex flex-wrap gap-4">
                  <StatCard 
                    title="Current Reward" 
                    value={trainingResults.finalReward.toFixed(2)} 
                    trend={trainingResults.finalReward > 0 ? 'up' : 'down'} 
                    trendValue={`${Math.abs(trainingResults.finalReward).toFixed(2)}`}
                  />

                  <StatCard 
                    title="Avg Last 10 Episodes" 
                    value={trainingResults.avgLast10.toFixed(2)} 
                    trend={trainingResults.avgLast10 > 0 ? 'up' : 'down'} 
                    trendValue={`${Math.abs(trainingResults.avgLast10).toFixed(2)}`}
                  />

                  <StatCard 
                    title="Improvement Over Baseline" 
                    value={`${trainingResults.improvementOverBaseline.toFixed(2)}%`} 
                    trend={trainingResults.improvementOverBaseline > 0 ? 'up' : 'down'} 
                    trendValue={`${Math.abs(trainingResults.improvementOverBaseline).toFixed(2)}%`}
                  />
                </div>
              </div>
            ) : (
              <div className="flex flex-col sm:flex-row items-center gap-4">
                <div className="w-full sm:w-64">
                  <label htmlFor="episodes" className="block text-sm font-medium text-gray-700 mb-1">
                    Training Episodes
                  </label>
                  <input
                    type="number"
                    id="episodes"
                    min="10"
                    max="1000"
                    value={trainingEpisodes}
                    onChange={handleEpisodesChange}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500"
                  />
                </div>

                <Button 
                  onClick={handleStartTraining} 
                  variant="primary"
                  className="w-full sm:w-auto mt-4 sm:mt-0 sm:self-end"
                >
                  Start Training
                </Button>
              </div>
            )}
          </div>
        </Card>

        {/* Main Dashboard Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          {/* Revenue vs Baseline */}
          <Card className="col-span-1 lg:col-span-2">
            <div className="p-4">
              <h2 className="text-xl font-semibold mb-4">Revenue vs Baseline</h2>
              <div className="h-80">
                <RevenueVsBaselineChart 
                  agentRewards={trainingResults.rewardHistory} 
                  baselineRewards={trainingResults.baselineHistory}
                />
              </div>
            </div>
          </Card>

          {/* Price-Demand-Revenue Relationship */}
          <Card>
            <div className="p-4">
              <h2 className="text-xl font-semibold mb-4">Price-Demand-Revenue Relationship</h2>
              <div className="h-80">
                <PriceDemandRevenueChart data={priceDemandData} />
              </div>
            </div>
          </Card>

          {/* Customer Segments */}
          <Card>
            <div className="p-4">
              <h2 className="text-xl font-semibold mb-4">Customer Segments</h2>
              <div className="h-80">
                <CustomerSegmentPie data={customerSegments} />
              </div>
            </div>
          </Card>

          {/* Product Performance */}
          <Card>
            <div className="p-4">
              <h2 className="text-xl font-semibold mb-4">Product Performance</h2>
              <div className="h-80">
                <ProductPerformanceChart products={products} />
              </div>
            </div>
          </Card>

          {/* Time-Based Pricing */}
          <Card>
            <div className="p-4">
              <h2 className="text-xl font-semibold mb-4">Time-Based Pricing</h2>
              <div className="h-80">
                <TimePricingChart data={timePricingData} />
              </div>
            </div>
          </Card>

          {/* Customer Retention */}
          <Card>
            <div className="p-4">
              <h2 className="text-xl font-semibold mb-4">Customer Retention</h2>
              <div className="h-80">
                <CustomerRetentionChart />
              </div>
            </div>
          </Card>

          {/* Price Sensitivity Heatmap */}
          <Card className="col-span-1 lg:col-span-2">
            <div className="p-4">
              <h2 className="text-xl font-semibold mb-4">Price Sensitivity Heatmap</h2>
              <div className="h-80">
                <PriceSensitivityHeatmap products={products} />
              </div>
            </div>
          </Card>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
