// src/pages/Dashboard.tsx
import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Card from '../components/Card';
import StatCard from '../components/StatCard';
import RevenueVsBaselineChart from '../components/RevenueVsBaselineChart';
import PriceDemandRevenueChart from '../components/PriceDemandRevenueChart';
import CustomerSegmentPie from '../components/CustomerSegmentPie';
import ProductPerformanceChart from '../components/ProductPerformanceChart';
import TimePricingChart from '../components/TimePricingChart';
import CustomerRetentionChart from '../components/CustomerRetentionChart';
import PriceSensitivityHeatmap from '../components/PriceSensitivityHeatmap';
import Button from '../components/Button';
import LoadingSpinner from '../components/LoadingSpinner';
import apiClient from '../api/apiClient';

interface TrainingStatus {
  isTraining: boolean;
  currentEpisode: number;
  totalEpisodes: number;
}

interface TrainingResults {
  finalReward: number;
  avgLast10: number;
  improvementOverBaseline: number;
  rewardHistory: number[];
  baselineHistory: number[];
}

interface PriceDemandPoint {
  price: number;
  demand: number;
  revenue: number;
}

interface TimePricingData {
  timeSlots: string[];
  prices: number[];
}

interface Segment {
  name: string;
  percentage: number;
  color?: string;
}

const Dashboard: React.FC = () => {
  const [products, setProducts] = useState<any[]>([]);
  const [customerSegments, setCustomerSegments] = useState<Segment[]>([]);
  const [priceDemandData, setPriceDemandData] = useState<PriceDemandPoint[]>([]);
  const [timePricingData, setTimePricingData] = useState<TimePricingData>({ timeSlots: [], prices: [] });
  const [trainingStatus, setTrainingStatus] = useState<TrainingStatus>({ isTraining: false, currentEpisode: 0, totalEpisodes: 0 });
  const [trainingResults, setTrainingResults] = useState<TrainingResults>({
    finalReward: 0, avgLast10: 0, improvementOverBaseline: 0, rewardHistory: [], baselineHistory: []
  });
  const [trainingEpisodes, setTrainingEpisodes] = useState<number>(100);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  const sanitizeResults = (raw: any): TrainingResults => ({
    finalReward: Number(raw?.finalReward) || 0,
    avgLast10: Number(raw?.avgLast10) || 0,
    improvementOverBaseline: Number(raw?.improvementOverBaseline) || 0,
    rewardHistory: Array.isArray(raw?.rewardHistory) ? raw.rewardHistory.map((v: any) => Number(v) || 0) : [],
    baselineHistory: Array.isArray(raw?.baselineHistory) ? raw.baselineHistory.map((v: any) => Number(v) || 0) : []
  });

  useEffect(() => {
    let poll: NodeJS.Timeout;
    const loadAll = async () => {
      try {
        setLoading(true);
        setError(null);
        const [prod, segRaw, pdRaw, tpRaw, statusRaw, resultsRaw] = await Promise.all([
          apiClient.getProducts(),
          apiClient.getCustomerSegments(),
          apiClient.getPriceDemandData(),
          apiClient.getTimePricingData(),
          apiClient.getTrainingStatus(),
          apiClient.getTrainingResults(),
        ]);

        setProducts(Array.isArray(prod) ? prod : []);

        const segments: Segment[] = Array.isArray(segRaw) ? segRaw.map((s: any) => ({
          name: s.name,
          percentage: (typeof s.size === 'number' ? s.size : 0) * 100,
          color: undefined
        })) : [];
        setCustomerSegments(segments);

        if (Array.isArray(pdRaw) && pdRaw.length) {
          const first = pdRaw[0];
          setPriceDemandData(first.pricePoints.map((p: any, i: number) => ({
            price: Number(p) || 0,
            demand: Number(first.demand[i]) || 0,
            revenue: Number(first.revenue[i]) || 0
          })));
        }

        setTimePricingData({
          timeSlots: Array.isArray(tpRaw?.timeOfDay) ? tpRaw.timeOfDay : [],
          prices: Array.isArray(tpRaw?.priceMultipliers) ? tpRaw.priceMultipliers : []
        });

        setTrainingStatus(statusRaw);
        setTrainingResults(sanitizeResults(resultsRaw));
      } catch (e) {
        console.error(e);
        setError('Failed to load data.');
      } finally {
        setLoading(false);
      }
    };

    loadAll();
    poll = setInterval(async () => {
      try {
        const statusRaw = await apiClient.getTrainingStatus();
        setTrainingStatus(statusRaw);
        if (statusRaw.isTraining) {
          const resultsRaw = await apiClient.getTrainingResults();
          setTrainingResults(sanitizeResults(resultsRaw));
        }
      } catch {}
    }, 2000);

    return () => clearInterval(poll);
  }, []);

  const handleStart = async () => {
    try {
      setError(null);
      await apiClient.startTraining({ episodes: trainingEpisodes, useBaseline: true, baselineStrategy: 'combined' });
      const statusRaw = await apiClient.getTrainingStatus();
      setTrainingStatus(statusRaw);
    } catch (e) {
      console.error(e);
      setError('Failed to start training.');
    }
  };

  const { finalReward, avgLast10, improvementOverBaseline, rewardHistory, baselineHistory } = trainingResults;
  const progressPct = trainingStatus.isTraining
    ? Math.round((trainingStatus.currentEpisode / trainingStatus.totalEpisodes) * 100)
    : 0;

  if (loading) {
    return <div className="min-h-screen flex items-center justify-center"><LoadingSpinner size="large"/></div>;
  }
  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center p-4">
        <Card className="max-w-md w-full p-6 text-center">
          <h2 className="text-xl mb-2">Error</h2>
          <p className="mb-4">{error}</p>
          <Button onClick={() => window.location.reload()} variant="primary">Retry</Button>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header title="Smart Dynamic Pricing Dashboard"/>
      <div className="container mx-auto px-4 py-6 space-y-6">
        <Card>
          <div className="flex flex-col md:flex-row md:justify-between p-4">
            <div>
              <h2 className="text-lg font-semibold">AI Training</h2>
              {trainingStatus.isTraining
                ? <p className="text-sm">Episode {trainingStatus.currentEpisode}/{trainingStatus.totalEpisodes} ({progressPct}%)</p>
                : <p className="text-sm">Not training</p>
              }
            </div>
            <div className="mt-4 md:mt-0 flex items-center gap-2">
              {!trainingStatus.isTraining && (
                <>
                  <input type="number" min={10} max={1000} value={trainingEpisodes}
                         onChange={e => setTrainingEpisodes(Number(e.target.value))}
                         className="w-20 p-2 border rounded"/>
                  <Button onClick={handleStart} variant="primary">Start</Button>
                </>
              )}
            </div>
          </div>
          {trainingStatus.isTraining && (
            <div className="h-2 w-full bg-gray-200 rounded overflow-hidden">
              <div className="h-full bg-blue-600" style={{ width: `${progressPct}%` }}/>
            </div>
          )}
          {trainingStatus.isTraining && (
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 p-4">
              <StatCard title="Final Reward" value={finalReward.toFixed(2)} trend={finalReward>0?'up':'down'} trendValue={finalReward.toFixed(2)}/>
              <StatCard title="Avg Last 10" value={avgLast10.toFixed(2)} trend={avgLast10>0?'up':'down'} trendValue={avgLast10.toFixed(2)}/>
              <StatCard title="Improvement" value={`${improvementOverBaseline.toFixed(2)}%`} trend={improvementOverBaseline>0?'up':'down'} trendValue={`${improvementOverBaseline.toFixed(2)}%`}/>
            </div>
          )}
        </Card>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <Card className="lg:col-span-2 p-4">
            <h3 className="text-lg font-semibold mb-2">Revenue vs. Baseline</h3>
            {rewardHistory.length && baselineHistory.length
              ? <RevenueVsBaselineChart agentRewards={rewardHistory} baselineRewards={baselineHistory}/>
              : <div className="h-64 flex items-center justify-center text-gray-500">No data</div>
            }
          </Card>

          <Card className="p-4">
            <h3 className="text-lg font-semibold mb-2">Price–Demand–Revenue</h3>
            {priceDemandData.length
              ? <PriceDemandRevenueChart data={priceDemandData}/>
              : <div className="h-64 flex items-center justify-center text-gray-500">No data</div>
            }
          </Card>

          <Card className="p-4">
            <h3 className="text-lg font-semibold mb-2">Customer Segments</h3>
            {customerSegments.length
              ? <CustomerSegmentPie data={customerSegments}/>
              : <div className="h-64 flex items-center justify-center text-gray-500">No data</div>
            }
          </Card>

          <Card className="p-4">
            <h3 className="text-lg font-semibold mb-2">Product Performance</h3>
            {products.length
              ? <ProductPerformanceChart products={products}/>
              : <div className="h-64 flex items-center justify-center text-gray-500">No data</div>
            }
          </Card>

          <Card className="p-4">
            <h3 className="text-lg font-semibold mb-2">Time‑Based Pricing</h3>
            {timePricingData.timeSlots.length
              ? <TimePricingChart data={timePricingData}/>
              : <div className="h-64 flex items-center justify-center text-gray-500">No data</div>
            }
          </Card>

          <Card className="p-4">
            <h3 className="text-lg font-semibold mb-2">Customer Retention</h3>
            <CustomerRetentionChart />
          </Card>

          <Card className="lg:col-span-2 p-4">
            <h3 className="text-lg font-semibold mb-2">Price Sensitivity Heatmap</h3>
            {products.length
              ? <PriceSensitivityHeatmap products={products}/>
              : <div className="h-64 flex items-center justify-center text-gray-500">No data</div>
            }
          </Card>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
