import React, { useState, useEffect } from 'react';

type ProductPerformanceChartProps = {
  products?: any[];
};

const ProductPerformanceChart: React.FC<ProductPerformanceChartProps> = ({ products = [] }) => {
  const [chartData, setChartData] = useState<{
    labels: string[];
    datasets: {
      label: string;
      data: number[];
      backgroundColor: string[];
      borderColor: string[];
      borderWidth: number;
    }[];
  }>({
    labels: [],
    datasets: []
  });

  useEffect(() => {
    if (products && products.length > 0) {
      // Process the data for the chart
      const productNames = products.map(product => product.name || 'Unknown Product');
      const productRevenues = products.map(product => product.revenue || Math.random() * 1000);

      // Generate colors
      const backgroundColors = products.map((_, index) => {
        const hue = (index * 137) % 360; // Golden angle approximation for good color distribution
        return `hsla(${hue}, 70%, 60%, 0.7)`;
      });

      const borderColors = backgroundColors.map(color => color.replace('0.7', '1'));

      setChartData({
        labels: productNames,
        datasets: [
          {
            label: 'Revenue',
            data: productRevenues,
            backgroundColor: backgroundColors,
            borderColor: borderColors,
            borderWidth: 1
          }
        ]
      });
    } else {
      // Generate sample data if none provided
      const sampleProducts = ['Product A', 'Product B', 'Product C', 'Product D', 'Product E'];
      const sampleRevenues = [1200, 1900, 800, 1400, 1100];

      // Generate colors
      const backgroundColors = sampleProducts.map((_, index) => {
        const hue = (index * 137) % 360;
        return `hsla(${hue}, 70%, 60%, 0.7)`;
      });

      const borderColors = backgroundColors.map(color => color.replace('0.7', '1'));

      setChartData({
        labels: sampleProducts,
        datasets: [
          {
            label: 'Revenue',
            data: sampleRevenues,
            backgroundColor: backgroundColors,
            borderColor: borderColors,
            borderWidth: 1
          }
        ]
      });
    }
  }, [products]);

  // In a real implementation, we would use Chart.js
  // For this example, we'll create a simplified visual representation
  return (
    <div className="h-full relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-r from-green-50 to-teal-50 opacity-50 rounded-lg"></div>
      <div className="relative h-full flex flex-col">
        <div className="flex justify-between items-center px-4 py-2">
          <div className="text-sm font-medium">Product Performance</div>
        </div>

        <div className="flex-1 p-4">
          <div className="h-full flex items-end">
            {chartData.labels.map((label, index) => {
              const value = chartData.datasets[0]?.data[index] || 0;
              const maxValue = Math.max(...(chartData.datasets[0]?.data || [1]));
              const height = maxValue > 0 ? (value / maxValue) * 100 : 0;
              const backgroundColor = chartData.datasets[0]?.backgroundColor[index] || 'rgba(75, 192, 192, 0.7)';

              return (
                <div key={index} className="flex-1 flex flex-col items-center">
                  <div className="w-full flex justify-center items-end h-[85%]">
                    <div 
                      className="w-4/5 rounded-t-sm transition-all duration-500 ease-out"
                      style={{
                        height: `${height}%`,
                        backgroundColor,
                        transform: 'scaleY(1)',
                        transformOrigin: 'bottom',
                        animation: `growUp 1s ease-out ${index * 0.1}s forwards`
                      }}
                    >
                      <div className="text-xs text-white text-center font-medium mt-1">
                        ${value.toFixed(0)}
                      </div>
                    </div>
                  </div>
                  <div className="text-xs text-gray-500 mt-1 truncate w-full text-center">
                    {label}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
      <style jsx>{`
        @keyframes growUp {
          from { transform: scaleY(0); }
          to { transform: scaleY(1); }
        }
      `}</style>
    </div>
  );
};

export default ProductPerformanceChart;
