// src/components/TimePricingChart.tsx
import React from 'react';

type TimePricingChartProps = {
  data?: { timeSlots?: string[]; prices?: number[] };
};

const TimePricingChart: React.FC<TimePricingChartProps> = ({ data }) => {
  // Guard
  if (
    !data ||
    !Array.isArray(data.timeSlots) ||
    !Array.isArray(data.prices) ||
    data.timeSlots.length === 0
  ) {
    return (
      <div className="h-80 flex items-center justify-center bg-gray-100 text-gray-500 rounded-lg">
        No data available
      </div>
    );
  }

  const labels = data.timeSlots!;
  const vals = data.prices!.map((v) => (typeof v === 'number' ? v : 0));
  const maxV = Math.max(...vals, 1);
  const minV = Math.min(...vals, 0);
  const range = maxV - minV;

  return (
    <div className="h-80 relative overflow-hidden rounded-lg bg-white shadow">
      <div className="absolute inset-0 bg-gradient-to-r from-purple-50 to-indigo-50 opacity-50 rounded-lg"></div>
      <div className="relative h-full flex flex-col">
        {/* Legend */}
        <div className="flex items-center gap-2 px-4 py-2 text-xs">
          Optimal Price
        </div>

        <div className="flex-1 p-4 flex items-end">
          {labels.map((lbl, i) => {
            const v = vals[i];
            const pct = range > 0 ? ((v - minV) / range) * 80 + 20 : 50;
            return (
              <div key={i} className="flex-1 flex flex-col items-center">
                <div className="relative w-4 h-4 mb-[-8px]">
                  <div
                    className="w-full h-full bg-purple-500 rounded-full"
                    style={{
                      bottom: `${pct}%`,
                      position: 'absolute',
                    }}
                  />
                  <div className="absolute top-[-25px] left-1/2 transform -translate-x-1/2 bg-purple-100 text-purple-800 text-xs font-medium px-2 py-0.5 rounded">
                    ${typeof v === 'number' ? v.toFixed(0) : '0'}
                  </div>
                </div>
                <div className="mt-1 text-xs text-gray-500 truncate w-full text-center">
                  {lbl}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default TimePricingChart;
