import React, { useState, useEffect } from 'react';

type CustomerRetentionChartProps = {
  data?: {
    months: string[];
    retention: number[];
  };
};

const CustomerRetentionChart: React.FC<CustomerRetentionChartProps> = ({ data }) => {
  const [chartData, setChartData] = useState<{
    labels: string[];
    datasets: {
      label: string;
      data: number[];
      borderColor: string;
      backgroundColor: string;
      borderWidth: number;
      tension: number;
    }[];
  }>({
    labels: [],
    datasets: []
  });

  useEffect(() => {
    if (data && data.months && data.retention) {
      setChartData({
        labels: data.months,
        datasets: [
          {
            label: 'Customer Retention',
            data: data.retention,
            borderColor: 'rgba(54, 162, 235, 1)',
            backgroundColor: 'rgba(54, 162, 235, 0.2)',
            borderWidth: 2,
            tension: 0.4,
          }
        ]
      });
    } else {
      // Generate sample data if none provided
      const months = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun'];
      const retention = [95, 92, 88, 90, 93, 95];

      setChartData({
        labels: months,
        datasets: [
          {
            label: 'Customer Retention',
            data: retention,
            borderColor: 'rgba(54, 162, 235, 1)',
            backgroundColor: 'rgba(54, 162, 235, 0.2)',
            borderWidth: 2,
            tension: 0.4,
          }
        ]
      });
    }
  }, [data]);

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top' as const,
      },
      tooltip: {
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
        titleColor: '#fff',
        bodyColor: '#fff',
        borderColor: 'rgba(0, 0, 0, 0.1)',
        borderWidth: 1,
        padding: 10,
        usePointStyle: true,
      },
    },
    scales: {
      y: {
        beginAtZero: false,
        min: 80,
        max: 100,
        title: {
          display: true,
          text: 'Retention Rate (%)'
        }
      },
      x: {
        title: {
          display: true,
          text: 'Month'
        }
      }
    },
    animation: {
      duration: 1000,
      easing: 'easeOutQuart' as const,
    },
  };

  // In a real implementation, we would use Chart.js
  // For this example, we'll create a simplified visual representation
  return (
    <div className="h-full relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-r from-blue-50 to-indigo-50 opacity-50 rounded-lg"></div>
      <div className="relative h-full flex flex-col">
        <div className="flex justify-between items-center px-4 py-2">
          <div className="flex flex-wrap gap-2">
            {chartData.datasets.map((dataset, index) => (
              <div key={index} className="flex items-center">
                <div
                  className="w-3 h-3 rounded-full mr-1"
                  style={{ backgroundColor: dataset.borderColor }}
                ></div>
                <span className="text-xs font-medium">{dataset.label}</span>
              </div>
            ))}
          </div>
        </div>
        <div className="flex-1 p-4">
          <div className="h-full flex items-end">
            {chartData.labels.map((label, labelIndex) => {
              const value = chartData.datasets[0]?.data[labelIndex] || 0;
              // Scale to fit in the chart area (80-100%)
              const height = ((value - 80) / 20) * 100;

              return (
                <div key={labelIndex} className="flex-1 flex flex-col items-center">
                  <div className="w-full flex justify-center items-end h-[85%]">
                    <div 
                      className="w-4/5 rounded-t-sm transition-all duration-500 ease-out"
                      style={{
                        height: `${height}%`,
                        backgroundColor: 'rgba(54, 162, 235, 0.7)',
                        transform: 'scaleY(1)',
                        transformOrigin: 'bottom',
                        animation: 'growUp 1s ease-out'
                      }}
                    >
                      <div className="text-xs text-white text-center font-medium">
                        {value}%
                      </div>
                    </div>
                  </div>
                  <div className="text-xs text-gray-500 mt-1 truncate w-full text-center">
                    {label}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
      <style jsx>{`
        @keyframes growUp {
          from { transform: scaleY(0); }
          to { transform: scaleY(1); }
        }
      `}</style>
    </div>
  );
};

export default CustomerRetentionChart;
