// src/pages/Dashboard.tsx
import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Card from '../components/Card';
import Button from '../components/Button';
import LoadingSpinner from '../components/LoadingSpinner';
import StatCard from '../components/StatCard';
import apiClient from '../api/apiClient';

import RevenueVsBaselineChart from '../components/RevenueVsBaselineChart';
import PriceDemandRevenueChart from '../components/PriceDemandRevenueChart';
import CustomerSegmentPie from '../components/CustomerSegmentPie';
import ProductPerformanceChart from '../components/ProductPerformanceChart';
import TimePricingChart from '../components/TimePricingChart';
import CustomerRetentionChart from '../components/CustomerRetentionChart';
import PriceSensitivityHeatmap from '../components/PriceSensitivityHeatmap';

type Product = {
  name: string;
  current_price: number;
  stock: number;
};

type Segment = {
  name: string;
  percentage: number;
  color?: string;
};

type PriceDemandPoint = {
  price: number;
  demand: number;
  revenue: number;
};

type HeatmapDatum = {
  product: string;
  pricePoints: number[];
  demand: number[];
};

type TimePricingData = {
  timeOfDay: string[];
  priceMultipliers: number[];
};

type TrainingStatus = {
  isTraining: boolean;
  currentEpisode: number;
  totalEpisodes: number;
};

type TrainingResults = {
  finalReward: number;
  avgLast10: number;
  improvementOverBaseline: number;
  rewardHistory: number[];
  baselineHistory: number[];
};

const Dashboard: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [segments, setSegments] = useState<Segment[]>([]);
  const [priceDemandData, setPriceDemandData] = useState<PriceDemandPoint[]>([]);
  const [heatmapData, setHeatmapData] = useState<HeatmapDatum[]>([]);
  const [timePricing, setTimePricing] = useState<TimePricingData>({ timeOfDay: [], priceMultipliers: [] });
  const [trainingStatus, setTrainingStatus] = useState<TrainingStatus>({ isTraining: false, currentEpisode: 0, totalEpisodes: 0 });
  const [trainingResults, setTrainingResults] = useState<TrainingResults>({
    finalReward: 0,
    avgLast10: 0,
    improvementOverBaseline: 0,
    rewardHistory: [],
    baselineHistory: []
  });
  const [trainingEpisodes, setTrainingEpisodes] = useState<number>(100);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Helper to coerce results
  const sanitizeResults = (raw: any): TrainingResults => ({
    finalReward: Number(raw.finalReward) || 0,
    avgLast10: Number(raw.avgLast10) || 0,
    improvementOverBaseline: Number(raw.improvementOverBaseline) || 0,
    rewardHistory: Array.isArray(raw.rewardHistory) ? raw.rewardHistory.map((v: any) => Number(v) || 0) : [],
    baselineHistory: Array.isArray(raw.baselineHistory) ? raw.baselineHistory.map((v: any) => Number(v) || 0) : []
  });

  // Initial load
  useEffect(() => {
    let poll: NodeJS.Timeout;

    const loadAll = async () => {
      try {
        setLoading(true);
        setError(null);

        const [
          prodRaw,
          segRaw,
          pdRaw,
          tpRaw,
          statusRaw,
          resultsRaw
        ] = await Promise.all([
          apiClient.getProducts(),
          apiClient.getCustomerSegments(),
          apiClient.getPriceDemandData(),
          apiClient.getTimePricingData(),
          apiClient.getTrainingStatus(),
          apiClient.getTrainingResults()
        ]);

        // Products
        setProducts(prodRaw);

        // Customer Segments → Pie
        const segs = Array.isArray(segRaw)
          ? segRaw.map((s: any) => ({
              name: s.name,
              percentage: (typeof s.size === 'number' ? s.size : 0) * 100,
              color: undefined
            }))
          : [];
        setSegments(segs);

        // Price–Demand–Revenue (bar) & Heatmap
        if (Array.isArray(pdRaw) && pdRaw.length > 0) {
          // first product for bar chart
          const first = pdRaw[0];
          setPriceDemandData(
            first.pricePoints.map((p: number, i: number) => ({
              price: p,
              demand: first.demand[i],
              revenue: first.revenue[i]
            }))
          );
          // all products for heatmap
          setHeatmapData(
            pdRaw.map((d: any) => ({
              product: d.product,
              pricePoints: d.pricePoints,
              demand: d.demand
            }))
          );
        }

        // Time-based pricing
        setTimePricing({
          timeOfDay: Array.isArray(tpRaw.timeOfDay) ? tpRaw.timeOfDay : [],
          priceMultipliers: Array.isArray(tpRaw.priceMultipliers) ? tpRaw.priceMultipliers : []
        });

        // Training
        setTrainingStatus(statusRaw);
        setTrainingResults(sanitizeResults(resultsRaw));
      } catch (e) {
        console.error(e);
        setError('Failed to load dashboard data.');
      } finally {
        setLoading(false);
      }
    };

    loadAll();

    // Poll training status/results every 2s
    poll = setInterval(async () => {
      try {
        const status = await apiClient.getTrainingStatus();
        setTrainingStatus(status);
        if (status.isTraining) {
          const results = await apiClient.getTrainingResults();
          setTrainingResults(sanitizeResults(results));
        }
      } catch {}
    }, 2000);

    return () => clearInterval(poll);
  }, []);

  const handleStartTraining = async () => {
    try {
      setError(null);
      await apiClient.startTraining({
        episodes: trainingEpisodes,
        useBaseline: true,
        baselineStrategy: 'combined'
      });
      const status = await apiClient.getTrainingStatus();
      setTrainingStatus(status);
    } catch (e) {
      console.error(e);
      setError('Failed to start training.');
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <LoadingSpinner size="large" />
      </div>
    );
  }
  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
        <Card className="max-w-md w-full text-center p-6">
          <h2 className="text-xl font-semibold mb-2">Error</h2>
          <p className="mb-4">{error}</p>
          <Button variant="primary" onClick={() => window.location.reload()}>
            Retry
          </Button>
        </Card>
      </div>
    );
  }

  const {
    finalReward,
    avgLast10,
    improvementOverBaseline,
    rewardHistory,
    baselineHistory
  } = trainingResults;

  const progressPct =
    trainingStatus.isTraining && trainingStatus.totalEpisodes > 0
      ? Math.round((trainingStatus.currentEpisode / trainingStatus.totalEpisodes) * 100)
      : 0;

  return (
    <div className="min-h-screen bg-gray-50">
      <Header title="Smart Dynamic Pricing Dashboard" />

      <div className="container mx-auto px-4 py-6 space-y-6">
        {/* Training Controls */}
        <Card>
          <div className="flex flex-col md:flex-row md:items-center md:justify-between p-4">
            <div>
              <h2 className="text-lg font-semibold">AI Training</h2>
              {trainingStatus.isTraining ? (
                <p className="text-sm text-gray-600">
                  Episode {trainingStatus.currentEpisode} of {trainingStatus.totalEpisodes} ({progressPct}
                  %)
                </p>
              ) : (
                <p className="text-sm text-gray-600">Not training</p>
              )}
            </div>
            <div className="mt-4 md:mt-0 flex items-center gap-2">
              {!trainingStatus.isTraining && (
                <>
                  <input
                    type="number"
                    min={10}
                    max={1000}
                    value={trainingEpisodes}
                    onChange={(e) => setTrainingEpisodes(Number(e.target.value))}
                    className="w-20 p-2 border rounded"
                  />
                  <Button onClick={handleStartTraining} variant="primary">
                    Start
                  </Button>
                </>
              )}
            </div>
          </div>
          {trainingStatus.isTraining && (
            <div className="h-2 w-full bg-gray-200 rounded overflow-hidden">
              <div
                className="h-full bg-blue-600 transition-all"
                style={{ width: `${progressPct}%` }}
              />
            </div>
          )}

          {trainingStatus.isTraining && (
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 p-4">
              <StatCard
                title="Final Reward"
                value={finalReward.toFixed(2)}
                trend={finalReward > 0 ? 'up' : 'down'}
                trendValue={Math.abs(finalReward).toFixed(2)}
              />
              <StatCard
                title="Avg Last 10"
                value={avgLast10.toFixed(2)}
                trend={avgLast10 > 0 ? 'up' : 'down'}
                trendValue={Math.abs(avgLast10).toFixed(2)}
              />
              <StatCard
                title="Improvement"
                value={`${improvementOverBaseline.toFixed(2)}%`}
                trend={improvementOverBaseline > 0 ? 'up' : 'down'}
                trendValue={`${Math.abs(improvementOverBaseline).toFixed(2)}%`}
              />
            </div>
          )}
        </Card>

        {/* Charts Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <Card className="lg:col-span-2 p-4">
            <h3 className="text-lg font-semibold mb-2">Revenue vs Baseline (Cumulative)</h3>
            <RevenueVsBaselineChart
              agentRewards={rewardHistory}
              baselineRewards={baselineHistory}
            />
          </Card>

          <Card className="p-4">
            <h3 className="text-lg font-semibold mb-2">Price–Demand–Revenue</h3>
            <PriceDemandRevenueChart data={priceDemandData} />
          </Card>

          <Card className="p-4">
            <h3 className="text-lg font-semibold mb-2">Customer Segments</h3>
            <CustomerSegmentPie data={segments} />
          </Card>

          <Card className="p-4">
            <h3 className="text-lg font-semibold mb-2">Projected Revenue</h3>
            <ProductPerformanceChart products={products} />
          </Card>

          <Card className="p-4">
            <h3 className="text-lg font-semibold mb-2">Time-Based Pricing</h3>
            <TimePricingChart data={timePricing} />
          </Card>

          <Card className="p-4">
            <h3 className="text-lg font-semibold mb-2">Customer Retention</h3>
            <CustomerRetentionChart />
          </Card>

          <Card className="lg:col-span-2 p-4">
            <h3 className="text-lg font-semibold mb-2">Price Sensitivity Heatmap</h3>
            <PriceSensitivityHeatmap data={heatmapData} />
          </Card>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
