import React, { useState, useEffect } from 'react';

type PriceDemandRevenueChartProps = {
  data?: any[];
};

const PriceDemandRevenueChart: React.FC<PriceDemandRevenueChartProps> = ({ data = [] }) => {
  const [chartData, setChartData] = useState<{
    labels: string[];
    datasets: {
      label: string;
      data: number[];
      borderColor: string;
      backgroundColor: string;
      borderWidth: number;
      yAxisID: string;
    }[];
  }>({
    labels: [],
    datasets: []
  });

  useEffect(() => {
    if (data && data.length > 0) {
      // Process the data for the chart
      const prices = data.map(item => item.price);
      const demand = data.map(item => item.demand);
      const revenue = data.map(item => item.price * item.demand);

      setChartData({
        labels: prices.map(price => `$${price.toFixed(2)}`),
        datasets: [
          {
            label: 'Demand',
            data: demand,
            borderColor: 'rgba(54, 162, 235, 1)',
            backgroundColor: 'rgba(54, 162, 235, 0.2)',
            borderWidth: 2,
            yAxisID: 'y'
          },
          {
            label: 'Revenue',
            data: revenue,
            borderColor: 'rgba(255, 99, 132, 1)',
            backgroundColor: 'rgba(255, 99, 132, 0.2)',
            borderWidth: 2,
            yAxisID: 'y1'
          }
        ]
      });
    } else {
      // Generate sample data if none provided
      const samplePrices = [50, 60, 70, 80, 90, 100, 110, 120, 130, 140, 150];
      const sampleDemand = samplePrices.map(price => Math.round(1000 * Math.exp(-0.01 * price)));
      const sampleRevenue = samplePrices.map((price, index) => price * sampleDemand[index]);

      setChartData({
        labels: samplePrices.map(price => `$${price.toFixed(2)}`),
        datasets: [
          {
            label: 'Demand',
            data: sampleDemand,
            borderColor: 'rgba(54, 162, 235, 1)',
            backgroundColor: 'rgba(54, 162, 235, 0.2)',
            borderWidth: 2,
            yAxisID: 'y'
          },
          {
            label: 'Revenue',
            data: sampleRevenue,
            borderColor: 'rgba(255, 99, 132, 1)',
            backgroundColor: 'rgba(255, 99, 132, 0.2)',
            borderWidth: 2,
            yAxisID: 'y1'
          }
        ]
      });
    }
  }, [data]);

  // In a real implementation, we would use Chart.js
  // For this example, we'll create a simplified visual representation
  return (
    <div className="h-full relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-r from-blue-50 to-pink-50 opacity-50 rounded-lg"></div>
      <div className="relative h-full flex flex-col">
        <div className="flex justify-between items-center px-4 py-2">
          <div className="flex flex-wrap gap-4">
            <div className="flex items-center">
              <div className="w-3 h-3 rounded-full mr-1 bg-[rgba(54,162,235,1)]"></div>
              <span className="text-xs font-medium">Demand</span>
            </div>
            <div className="flex items-center">
              <div className="w-3 h-3 rounded-full mr-1 bg-[rgba(255,99,132,1)]"></div>
              <span className="text-xs font-medium">Revenue</span>
            </div>
          </div>
        </div>

        <div className="flex-1 p-4">
          <div className="h-full flex items-end relative">
            {/* Y-axis labels */}
            <div className="absolute left-0 top-0 h-full flex flex-col justify-between">
              <span className="text-xs text-blue-500">Demand</span>
              <span className="text-xs text-pink-500">Revenue</span>
            </div>

            {/* Chart bars */}
            <div className="w-full h-full pl-12 flex items-end">
              {chartData.labels.map((label, index) => {
                const demandValue = chartData.datasets[0]?.data[index] || 0;
                const revenueValue = chartData.datasets[1]?.data[index] || 0;

                // Normalize values for display
                const maxDemand = Math.max(...(chartData.datasets[0]?.data || [1]));
                const maxRevenue = Math.max(...(chartData.datasets[1]?.data || [1]));

                const demandHeight = maxDemand > 0 ? (demandValue / maxDemand) * 80 : 0;
                const revenueHeight = maxRevenue > 0 ? (revenueValue / maxRevenue) * 80 : 0;

                return (
                  <div key={index} className="flex-1 flex flex-col items-center justify-end h-full">
                    <div className="relative w-full flex justify-center gap-1">
                      {/* Demand bar */}
                      <div 
                        className="w-3 bg-[rgba(54,162,235,0.7)] rounded-t transition-all duration-500 ease-out"
                        style={{ 
                          height: `${demandHeight}%`,
                          transform: 'scaleY(1)',
                          transformOrigin: 'bottom',
                          animation: `growUp 1s ease-out ${index * 0.05}s forwards`
                        }}
                      >
                        <div className="absolute top-[-20px] text-[0.6rem] text-blue-500 whitespace-nowrap">
                          {demandValue}
                        </div>
                      </div>

                      {/* Revenue bar */}
                      <div 
                        className="w-3 bg-[rgba(255,99,132,0.7)] rounded-t transition-all duration-500 ease-out"
                        style={{ 
                          height: `${revenueHeight}%`,
                          transform: 'scaleY(1)',
                          transformOrigin: 'bottom',
                          animation: `growUp 1s ease-out ${index * 0.05}s forwards`
                        }}
                      >
                        <div className="absolute top-[-20px] text-[0.6rem] text-pink-500 whitespace-nowrap">
                          ${revenueValue}
                        </div>
                      </div>
                    </div>

                    {/* X-axis label */}
                    <div className="text-xs text-gray-500 mt-1 truncate w-full text-center">
                      {label}
                    </div>
                  </div>
                );
              })}
            </div>
          </div>
        </div>
      </div>
      <style jsx>{`
        @keyframes growUp {
          from { transform: scaleY(0); }
          to { transform: scaleY(1); }
        }
      `}</style>
    </div>
  );
};

export default PriceDemandRevenueChart;
