import React from 'react';

export interface StatCardProps {
  title: string;
  value: string | number;
  trend?: 'up' | 'down';
  trendValue?: string;
}

const StatCard: React.FC<StatCardProps> = ({ title, value, trend, trendValue }) => (
  <div className="bg-white shadow rounded p-4 flex flex-col items-start">
    <h4 className="text-gray-600">{title}</h4>
    <p className="text-2xl font-semibold">{value}</p>
    {trend && trendValue && (
      <div className={`mt-2 inline-flex items-center text-sm font-medium ${trend === 'up' ? 'text-green-600' : 'text-red-600'}`}>
        {trend === 'up' ? '↑' : '↓'} <span className="ml-1">{trendValue}</span>
      </div>
    )}
  </div>
);

export default StatCard;
