// src/pages/Dashboard.tsx
import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Card from '../components/Card';
import StatCard from '../components/StatCard';
import RevenueVsBaselineChart from '../components/RevenueVsBaselineChart';
import PriceDemandRevenueChart from '../components/PriceDemandRevenueChart';
import CustomerSegmentPie from '../components/CustomerSegmentPie';
import ProductPerformanceChart from '../components/ProductPerformanceChart';
import TimePricingChart from '../components/TimePricingChart';
import CustomerRetentionChart from '../components/CustomerRetentionChart';
import PriceSensitivityHeatmap from '../components/PriceSensitivityHeatmap';
import Button from '../components/Button';
import LoadingSpinner from '../components/LoadingSpinner';
import apiClient from '../api/apiClient';

interface TrainingStatus {
  isTraining: boolean;
  currentEpisode: number;
  totalEpisodes: number;
}

interface TrainingResults {
  finalReward: number;
  avgLast10: number;
  improvementOverBaseline: number;
  rewardHistory: number[];
  baselineHistory: number[];
}

interface TimePricingData {
  timeSlots: string[];
  prices: number[];
}

const Dashboard: React.FC = () => {
  // Data state
  const [products, setProducts] = useState<any[]>([]);
  const [customerSegments, setCustomerSegments] = useState<any[]>([]);
  const [priceDemandData, setPriceDemandData] = useState<any[]>([]);
  const [timePricingData, setTimePricingData] = useState<TimePricingData>({ timeSlots: [], prices: [] });

  // Training state
  const [trainingStatus, setTrainingStatus] = useState<TrainingStatus>({
    isTraining: false,
    currentEpisode: 0,
    totalEpisodes: 0,
  });
  const [trainingResults, setTrainingResults] = useState<TrainingResults>({
    finalReward: 0,
    avgLast10: 0,
    improvementOverBaseline: 0,
    rewardHistory: [],
    baselineHistory: [],
  });

  // UI state
  const [trainingEpisodes, setTrainingEpisodes] = useState<number>(100);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  // Helpers
  const sanitizeResults = (raw: any): TrainingResults => {
    if (!raw) {
      return {
        finalReward: 0,
        avgLast10: 0,
        improvementOverBaseline: 0,
        rewardHistory: [],
        baselineHistory: [],
      };
    }

    return {
      finalReward: typeof raw.finalReward === 'number' ? raw.finalReward : 0,
      avgLast10: typeof raw.avgLast10 === 'number' ? raw.avgLast10 : 0,
      improvementOverBaseline: typeof raw.improvementOverBaseline === 'number' ? raw.improvementOverBaseline : 0,
      rewardHistory: Array.isArray(raw.rewardHistory)
        ? raw.rewardHistory.map((v: any) => (typeof v === 'number' ? v : 0))
        : [],
      baselineHistory: Array.isArray(raw.baselineHistory)
        ? raw.baselineHistory.map((v: any) => (typeof v === 'number' ? v : 0))
        : [],
    };
  };

  const sanitizeTimePricing = (raw: any): TimePricingData => {
    if (!raw) {
      return { timeSlots: [], prices: [] };
    }

    return {
      timeSlots: Array.isArray(raw.timeSlots) ? raw.timeSlots.map(String) : [],
      prices: Array.isArray(raw.prices) 
        ? raw.prices.map((v: any) => (typeof v === 'number' ? v : 0)) 
        : [],
    };
  };

  // Initial load + polling
  useEffect(() => {
    const loadAll = async () => {
      try {
        setLoading(true);
        setError(null);

        const [
          prod,
          seg,
          pd,
          tpRaw,
          statusRaw,
          trRaw
        ] = await Promise.all([
          apiClient.getProducts(),
          apiClient.getCustomerSegments(),
          apiClient.getPriceDemandData(),
          apiClient.getTimePricingData(),
          apiClient.getTrainingStatus(),
          apiClient.getTrainingResults()
        ]);

        setProducts(prod || []);
        setCustomerSegments(seg || []);
        setPriceDemandData(pd || []);
        setTimePricingData(sanitizeTimePricing(tpRaw));
        setTrainingStatus(statusRaw || { isTraining: false, currentEpisode: 0, totalEpisodes: 0 });
        setTrainingResults(sanitizeResults(trRaw));
      } catch (e) {
        console.error(e);
        setError('Failed to load dashboard data.');
      } finally {
        setLoading(false);
      }
    };

    loadAll();

    const interval = setInterval(async () => {
      try {
        const statusRaw = await apiClient.getTrainingStatus();
        setTrainingStatus(statusRaw || { isTraining: false, currentEpisode: 0, totalEpisodes: 0 });

        if (statusRaw?.isTraining) {
          const trRaw = await apiClient.getTrainingResults();
          setTrainingResults(sanitizeResults(trRaw));
        }

        const tpRaw = await apiClient.getTimePricingData();
        setTimePricingData(sanitizeTimePricing(tpRaw));
      } catch (e) {
        console.error(e);
      }
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  const handleStartTraining = async () => {
    try {
      setError(null);
      await apiClient.startTraining({
        episodes: trainingEpisodes,
        useBaseline: true,
        baselineStrategy: 'combined'
      });
      const statusRaw = await apiClient.getTrainingStatus();
      setTrainingStatus(statusRaw || { isTraining: false, currentEpisode: 0, totalEpisodes: 0 });
    } catch (e) {
      console.error(e);
      setError('Failed to start training.');
    }
  };

  // Safely format numbers
  const formatNumber = (value: number | undefined, decimals = 2): string => {
    if (value === undefined || value === null || isNaN(value)) {
      return '0.00';
    }
    return value.toFixed(decimals);
  };

  // Destructure so we never reference undefined
  const {
    finalReward = 0,
    avgLast10 = 0,
    improvementOverBaseline = 0,
    rewardHistory = [],
    baselineHistory = []
  } = trainingResults || {};

  const progressPct =
    trainingStatus.isTraining && trainingStatus.totalEpisodes > 0
      ? Math.round((trainingStatus.currentEpisode / trainingStatus.totalEpisodes) * 100)
      : 0;

  // Loading / Error UIs
  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100">
        <LoadingSpinner size="large" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
        <Card className="max-w-md w-full text-center p-6">
          <h2 className="text-xl font-semibold mb-2">Error</h2>
          <p className="mb-4">{error}</p>
          <Button onClick={() => window.location.reload()} variant="primary">
            Retry
          </Button>
        </Card>
      </div>
    );
  }

  // Main render
  return (
    <div className="min-h-screen bg-gray-100">
      <Header title="Smart Dynamic Pricing Dashboard" />

      <div className="container mx-auto px-4 py-6">
        {/* AI Training Controls */}
        <Card className="mb-6 p-4">
          <h2 className="text-xl font-semibold mb-4">AI Training Controls</h2>

          {trainingStatus.isTraining ? (
            <>
              <div className="mb-4">
                <div className="flex justify-between mb-1">
                  <span>Progress</span>
                  <span>{progressPct}%</span>
                </div>
                <div className="w-full bg-gray-200 h-2.5 rounded">
                  <div
                    className="bg-blue-600 h-2.5 rounded transition-all"
                    style={{ width: `${progressPct}%` }}
                  />
                </div>
              </div>

              <div className="flex flex-wrap gap-4">
                <StatCard
                  title="Final Reward"
                  value={formatNumber(finalReward)}
                  trend={finalReward > 0 ? 'up' : 'down'}
                  trendValue={formatNumber(Math.abs(finalReward))}
                />
                <StatCard
                  title="Avg Last 10"
                  value={formatNumber(avgLast10)}
                  trend={avgLast10 > 0 ? 'up' : 'down'}
                  trendValue={formatNumber(Math.abs(avgLast10))}
                />
                <StatCard
                  title="Improvement"
                  value={`${formatNumber(improvementOverBaseline)}%`}
                  trend={improvementOverBaseline > 0 ? 'up' : 'down'}
                  trendValue={`${formatNumber(Math.abs(improvementOverBaseline))}%`}
                />
              </div>
            </>
          ) : (
            <div className="flex flex-col sm:flex-row gap-4 items-center">
              <div>
                <label htmlFor="episodes" className="block mb-1">Episodes</label>
                <input
                  id="episodes"
                  type="number"
                  min={10}
                  max={1000}
                  value={trainingEpisodes}
                  onChange={(e) => setTrainingEpisodes(Number(e.target.value))}
                  className="border p-2 rounded w-20"
                />
              </div>
              <Button onClick={handleStartTraining} variant="primary">
                Start Training
              </Button>
            </div>
          )}
        </Card>

        {/* Dashboard Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <Card className="lg:col-span-2 p-4">
            <h2 className="text-xl font-semibold mb-4">Revenue vs Baseline</h2>
            <RevenueVsBaselineChart
              agentRewards={rewardHistory}
              baselineRewards={baselineHistory}
            />
          </Card>

          <Card className="p-4">
            <h2 className="text-xl font-semibold mb-4">Price‑Demand‑Revenue</h2>
            <PriceDemandRevenueChart data={priceDemandData} />
          </Card>

          <Card className="p-4">
            <h2 className="text-xl font-semibold mb-4">Customer Segments</h2>
            <CustomerSegmentPie data={customerSegments} />
          </Card>

          <Card className="p-4">
            <h2 className="text-xl font-semibold mb-4">Product Performance</h2>
            <ProductPerformanceChart products={products} />
          </Card>

          <Card className="p-4">
            <h2 className="text-xl font-semibold mb-4">Time‑Based Pricing</h2>
            <TimePricingChart data={timePricingData} />
          </Card>

          <Card className="p-4">
            <h2 className="text-xl font-semibold mb-4">Customer Retention</h2>
            <CustomerRetentionChart />
          </Card>

          <Card className="lg:col-span-2 p-4">
            <h2 className="text-xl font-semibold mb-4">Price Sensitivity Heatmap</h2>
            <PriceSensitivityHeatmap products={products} />
          </Card>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
