// src/components/PriceDemandRevenueChart.tsx
import React from 'react';

type PriceDemandRevenueChartProps = {
  data?: Array<{ price?: number; demand?: number }>;
};

const PriceDemandRevenueChart: React.FC<PriceDemandRevenueChartProps> = ({ data }) => {
  // Guard: no data → placeholder
  if (!Array.isArray(data) || data.length === 0) {
    return (
      <div className="h-80 flex items-center justify-center bg-gray-100 text-gray-500 rounded-lg">
        No data available
      </div>
    );
  }

  // Coerce values safely
  const prices = data.map((d) => (typeof d.price === 'number' ? d.price : 0));
  const demands = data.map((d) => (typeof d.demand === 'number' ? d.demand : 0));
  const revenues = prices.map((p, i) => p * demands[i]);

  const labels = prices.map((p) =>
    typeof p === 'number' ? `$${p.toFixed(2)}` : '$0.00'
  );

  const maxDemand = Math.max(...demands, 1);
  const maxRevenue = Math.max(...revenues, 1);

  return (
    <div className="h-80 relative overflow-hidden rounded-lg bg-white shadow">
      {/* Legend */}
      <div className="flex justify-center gap-8 py-2">
        <div className="flex items-center">
          <span className="w-3 h-3 bg-[rgba(54,162,235,1)] rounded-full mr-1" />
          <span className="text-xs font-medium">Demand</span>
        </div>
        <div className="flex items-center">
          <span className="w-3 h-3 bg-[rgba(255,99,132,1)] rounded-full mr-1" />
          <span className="text-xs font-medium">Revenue</span>
        </div>
      </div>

      {/* Bars */}
      <div className="flex-1 p-4 flex items-end h-[calc(100%-2.5rem)]">
        {labels.map((label, idx) => {
          const dVal = demands[idx];
          const rVal = revenues[idx];
          const dPct = (dVal / maxDemand) * 100;
          const rPct = (rVal / maxRevenue) * 100;

          return (
            <div key={idx} className="flex-1 flex flex-col items-center">
              <div className="flex items-end space-x-1 w-full h-4/5">
                <div
                  className="w-2 bg-[rgba(54,162,235,0.7)] rounded-t transition-all duration-500 ease-out"
                  style={{ height: `${dPct}%` }}
                  title={`Demand: ${dVal}`}
                />
                <div
                  className="w-2 bg-[rgba(255,99,132,0.7)] rounded-t transition-all duration-500 ease-out"
                  style={{ height: `${rPct}%` }}
                  title={`Revenue: $${typeof rVal === 'number' ? rVal.toFixed(2) : '0.00'}`}
                />
              </div>
              <div className="mt-1 text-xs text-gray-500 truncate">{label}</div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default PriceDemandRevenueChart;
