// src/components/StatCard.tsx
import React, { ReactNode } from 'react';
import { ArrowUp, ArrowDown } from 'lucide-react';
import { motion } from 'framer-motion';

export interface StatCardProps {
  title: string;
  value: string | number;
  trend?: 'up' | 'down';
  trendValue?: string | number;
  icon?: ReactNode;
}

const StatCard: React.FC<StatCardProps> = ({
  title,
  value,
  trend,
  trendValue,
  icon,
}) => {
  const isPositive = trend === 'up';

  return (
    <motion.div
      whileHover={{ y: -4 }}
      transition={{ type: 'spring', stiffness: 300, damping: 20 }}
      className="bg-white rounded-2xl shadow-soft-lg border border-gray-100 p-6 flex flex-col justify-between"
    >
      <div className="flex justify-between items-start">
        <div>
          <p className="text-sm font-semibold text-gray-500 uppercase tracking-wide">
            {title}
          </p>
          <p className="mt-2 text-4xl font-bold text-gray-900">{value}</p>
        </div>
        {icon && (
          <div className="p-3 bg-blue-50 rounded-full">
            {icon}
          </div>
        )}
      </div>

      {trend && trendValue != null && (
        <div className="mt-4 flex items-center">
          <span
            className={`
              inline-flex items-center space-x-1 px-2 py-1 rounded-full text-sm font-medium
              ${isPositive ? 'bg-green-100 text-green-800' : 'bg-red-100 text-red-800'}
            `}
          >
            {isPositive ? (
              <ArrowUp size={14} />
            ) : (
              <ArrowDown size={14} />
            )}
            <span>{trendValue}</span>
          </span>
          <span className="ml-3 text-xs text-gray-400">vs. last period</span>
        </div>
      )}
    </motion.div>
  );
};

export default StatCard;
