// src/components/ProductPerformanceChart.tsx
import React from 'react';

type Product = { name?: string; revenue?: number };

type ProductPerformanceChartProps = {
  products?: Product[];
};

const ProductPerformanceChart: React.FC<ProductPerformanceChartProps> = ({ products }) => {
  // Guard: no products → placeholder
  if (!Array.isArray(products) || products.length === 0) {
    return (
      <div className="h-full flex items-center justify-center bg-gray-100 text-gray-500 rounded-lg">
        No data available
      </div>
    );
  }

  const labels = products.map((p) => p.name || 'Unknown');
  const values = products.map((p) => (typeof p.revenue === 'number' ? p.revenue : 0));
  const maxVal = Math.max(...values, 1);

  return (
    <div className="h-full relative overflow-hidden rounded-lg bg-white shadow">
      <div className="absolute inset-0 bg-gradient-to-r from-green-50 to-teal-50 opacity-50 rounded-lg"></div>
      <div className="relative h-full flex flex-col">
        <div className="flex justify-between items-center px-4 py-2">
          <span className="text-sm font-medium">Product Revenue</span>
        </div>
        <div className="flex-1 p-4 flex items-end">
          {labels.map((lbl, i) => {
            const v = values[i];
            const pct = (v / maxVal) * 100;
            return (
              <div key={i} className="flex-1 flex flex-col items-center">
                <div
                  className="w-4/5 rounded-t-sm transition-all duration-500 ease-out bg-teal-600"
                  style={{ height: `${pct}%` }}
                >
                  <div className="text-xs text-white text-center">
                    ${typeof v === 'number' ? v.toFixed(0) : '0'}
                  </div>
                </div>
                <div className="mt-1 text-xs text-gray-500 truncate w-full text-center">
                  {lbl}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default ProductPerformanceChart;
