// src/components/RevenueVsBaselineChart.tsx
import React from 'react';

type RevenueVsBaselineChartProps = {
  agentRewards: number[];
  baselineRewards: number[];
};

const RevenueVsBaselineChart: React.FC<RevenueVsBaselineChartProps> = ({
  agentRewards = [],
  baselineRewards = []
}) => {
  // Guard: no episodes → placeholder
  if (agentRewards.length === 0 && baselineRewards.length === 0) {
    return (
      <div className="h-full flex items-center justify-center bg-gray-100 text-gray-500 rounded-lg">
        No training data available
      </div>
    );
  }

  const length = Math.max(agentRewards.length, baselineRewards.length);
  const labels = Array.from({ length }, (_, i) => `Ep ${i + 1}`);

  const cum = (arr: number[]) =>
    arr.reduce<number[]>((acc, v, i) => {
      const prev = acc[i - 1] || 0;
      acc.push(prev + (typeof v === 'number' ? v : 0));
      return acc;
    }, []);

  const aCum = cum(agentRewards);
  const bCum = cum(baselineRewards);

  const totalA = aCum[aCum.length - 1] || 0;
  const totalB = bCum[bCum.length - 1] || 0;
  const imp = totalB
    ? ((totalA - totalB) / Math.abs(totalB)) * 100
    : 0;

  const maxVal = Math.max(...aCum, ...bCum, 1);

  return (
    <div className="h-full relative overflow-hidden rounded-lg bg-white shadow">
      <div className="absolute inset-0 bg-gradient-to-r from-blue-50 to-teal-50 opacity-50 rounded-lg"></div>
      <div className="relative h-full flex flex-col">
        {/* Legend + improvement */}
        <div className="flex items-center gap-6 px-4 py-2">
          <div className="flex items-center gap-1">
            <span className="w-3 h-3 bg-teal-600 rounded-full" /> AI Agent
          </div>
          <div className="flex items-center gap-1">
            <span className="w-3 h-3 bg-pink-600 rounded-full" /> Baseline
          </div>
          <div className={`text-xs font-medium ${imp >= 0 ? 'text-green-600' : 'text-red-600'}`}>
            Improvement: {imp.toFixed(2)}%
          </div>
        </div>

        {/* Bars */}
        <div className="flex-1 p-4 flex items-end relative">
          {/* Y‑axis */}
          <div className="absolute left-0 top-0 h-full flex flex-col justify-between text-xs text-gray-500">
            {[0, 0.25, 0.5, 0.75, 1].map((t) => (
              <span key={t}>${(maxVal * (1 - t)).toFixed(0)}</span>
            ))}
          </div>

          <div className="w-full h-full pl-12 flex items-end gap-1">
            {aCum.map((val, i) => {
              const hA = ((val || 0) / maxVal) * 100;
              const bVal = bCum[i] || 0;
              const hB = (bVal / maxVal) * 100;
              return (
                <div key={i} className="flex-1 flex flex-col items-center">
                  <div className="flex items-end">
                    <div
                      className="w-1 bg-teal-600 rounded-t"
                      style={{ height: `${hA}%` }}
                    />
                    <div
                      className="w-1 bg-pink-600 rounded-t ml-1"
                      style={{ height: `${hB}%` }}
                    />
                  </div>
                  {i % Math.ceil(labels.length / 10) === 0 && (
                    <span className="mt-1 text-xs text-gray-500">{labels[i]}</span>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

export default RevenueVsBaselineChart;
