// src/pages/Dashboard.tsx

import React, { useState, useEffect } from 'react'
import Header from '../components/Header'
import Card from '../components/Card'
import StatCard from '../components/StatCard'
import RevenueVsBaselineChart from '../components/RevenueVsBaselineChart'
import PriceDemandRevenueChart from '../components/PriceDemandRevenueChart'
import CustomerSegmentPie from '../components/CustomerSegmentPie'
import ProductPerformanceChart from '../components/ProductPerformanceChart'
import TimePricingChart from '../components/TimePricingChart'
import CustomerRetentionChart from '../components/CustomerRetentionChart'
import PriceSensitivityHeatmap from '../components/PriceSensitivityHeatmap'
import Button from '../components/Button'
import LoadingSpinner from '../components/LoadingSpinner'
import apiClient from '../api/apiClient'

type TrainingStatus = {
  isTraining: boolean
  currentEpisode: number
  totalEpisodes: number
}
type TrainingResults = {
  finalReward: number
  avgLast10: number
  improvementOverBaseline: number
  rewardHistory: number[]
  baselineHistory: number[]
}
type PriceDemandRaw = {
  product: string
  pricePoints: number[]
  demand: number[]
  revenue: number[]
}
type TimePricingRaw = {
  timeOfDay: string[]
  priceMultipliers: number[]
}
type CustomerSegRaw = { name: string; size: number }
interface Segment {
  name: string
  percentage: number
  color?: string
}

const Dashboard: React.FC = () => {
  // State
  const [products, setProducts] = useState<any[]>([])
  const [priceDemandRaw, setPriceDemandRaw] = useState<PriceDemandRaw[]>([])
  const [selectedProductIdx, setSelectedProductIdx] = useState(0)
  const [customerSegments, setCustomerSegments] = useState<Segment[]>([])
  const [timePricingRaw, setTimePricingRaw] = useState<TimePricingRaw>({ timeOfDay: [], priceMultipliers: [] })
  const [trainingStatus, setTrainingStatus] = useState<TrainingStatus>({ isTraining: false, currentEpisode: 0, totalEpisodes: 0 })
  const [trainingResults, setTrainingResults] = useState<TrainingResults>({
    finalReward: 0, avgLast10: 0, improvementOverBaseline: 0, rewardHistory: [], baselineHistory: []
  })
  const [trainingEpisodes, setTrainingEpisodes] = useState(100)
  const [baselineStrategy, setBaselineStrategy] = useState<'fixed'|'adaptive'|'time'|'combined'>('combined')
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string|null>(null)

  const sanitizeResults = (raw: any): TrainingResults => ({
    finalReward: Number(raw.finalReward)||0,
    avgLast10: Number(raw.avgLast10)||0,
    improvementOverBaseline: Number(raw.improvementOverBaseline)||0,
    rewardHistory: Array.isArray(raw.rewardHistory)?raw.rewardHistory.map((v:any)=>Number(v)||0):[],
    baselineHistory: Array.isArray(raw.baselineHistory)?raw.baselineHistory.map((v:any)=>Number(v)||0):[]
  })

  // Load static data once
  useEffect(() => {
    const loadAll = async () => {
      try {
        setLoading(true)
        setError(null)
        const [prods, pdRaw, segRaw, tpRaw, statusRaw, resRaw] = await Promise.all([
          apiClient.getProducts(),
          apiClient.getPriceDemandData(),
          apiClient.getCustomerSegments(),
          apiClient.getTimePricingData(),
          apiClient.getTrainingStatus(),
          apiClient.getTrainingResults()
        ])
        setProducts(prods || [])
        setPriceDemandRaw(pdRaw || [])
        setCustomerSegments(
          (segRaw||[]).map((s:any) => ({ name: s.name, percentage: (s.size||0)*100 }))
        )
        setTimePricingRaw({
          timeOfDay: tpRaw.timeOfDay||[],
          priceMultipliers: tpRaw.priceMultipliers||[]
        })
        setTrainingStatus(statusRaw)
        setTrainingResults(sanitizeResults(resRaw))
      } catch (e) {
        console.error(e)
        setError('Failed to load dashboard data.')
      } finally {
        setLoading(false)
      }
    }
    loadAll()
  }, [])

  // Polling for live updates during training
  useEffect(() => {
    const iv = setInterval(async () => {
      if (!trainingStatus.isTraining) return
      try {
        const statusRaw = await apiClient.getTrainingStatus()
        setTrainingStatus(statusRaw)
        const resRaw = await apiClient.getTrainingResults()
        setTrainingResults(sanitizeResults(resRaw))
        const tpRaw = await apiClient.getTimePricingData()
        setTimePricingRaw({
          timeOfDay: tpRaw.timeOfDay||[],
          priceMultipliers: tpRaw.priceMultipliers||[]
        })
      } catch {}
    }, 2000)
    return () => clearInterval(iv)
  }, [trainingStatus.isTraining])

  const handleStartTraining = async () => {
    try {
      setError(null)
      await apiClient.startTraining({
        episodes: trainingEpisodes,
        useBaseline: true,
        baselineStrategy
      })
      const statusRaw = await apiClient.getTrainingStatus()
      setTrainingStatus(statusRaw)
    } catch (e) {
      console.error(e)
      setError('Failed to start training.')
    }
  }

  // Derived
  const { finalReward, avgLast10, improvementOverBaseline, rewardHistory, baselineHistory } = trainingResults
  const progressPct = trainingStatus.isTraining && trainingStatus.totalEpisodes>0
    ? Math.round( trainingStatus.currentEpisode / trainingStatus.totalEpisodes * 100 )
    : 0

  if (loading) {
    return <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <LoadingSpinner size="large"/>
    </div>
  }
  if (error) {
    return <div className="min-h-screen flex items-center justify-center bg-gray-100 p-4">
      <Card className="max-w-md w-full text-center p-6">
        <h2 className="text-xl font-semibold mb-2">Error</h2>
        <p className="mb-4">{error}</p>
        <Button variant="primary" onClick={()=>window.location.reload()}>Retry</Button>
      </Card>
    </div>
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <Header title="Smart Dynamic Pricing Dashboard"/>
      <div className="container mx-auto px-4 py-6 space-y-6">

        {/* Training Controls */}
        <Card>
          <div className="flex flex-col md:flex-row md:items-center md:justify-between p-4">
            <div>
              <h2 className="text-lg font-semibold">AI Training</h2>
              {trainingStatus.isTraining
                ? <p className="text-sm text-gray-600">Episode {trainingStatus.currentEpisode} of {trainingStatus.totalEpisodes} ({progressPct}%)</p>
                : <p className="text-sm text-gray-600">Not training</p>
              }
            </div>
            <div className="mt-4 md:mt-0 flex items-center gap-2">
              <select
                value={baselineStrategy}
                onChange={e => setBaselineStrategy(e.target.value as any)}
                className="p-2 border rounded"
              >
                <option value="fixed">Fixed</option>
                <option value="adaptive">Adaptive</option>
                <option value="time">Time</option>
                <option value="combined">Combined</option>
              </select>
              {!trainingStatus.isTraining && <>
                <input
                  type="number" min={10} max={1000} value={trainingEpisodes}
                  onChange={e=>setTrainingEpisodes(Number(e.target.value))}
                  className="w-20 p-2 border rounded"
                />
                <Button onClick={handleStartTraining} variant="primary">Start</Button>
              </>}
            </div>
          </div>
          {trainingStatus.isTraining && (
            <div className="h-2 w-full bg-gray-200 rounded overflow-hidden">
              <div
                className="h-full bg-blue-600 transition-all"
                style={{ width: `${progressPct}%` }}
              />
            </div>
          )}
          {trainingStatus.isTraining && (
            <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 p-4">
              <StatCard title="Final Reward" value={finalReward.toFixed(2)} trend={finalReward>0?'up':'down'} trendValue={Math.abs(finalReward).toFixed(2)}/>
              <StatCard title="Avg Last 10" value={avgLast10.toFixed(2)} trend={avgLast10>0?'up':'down'} trendValue={Math.abs(avgLast10).toFixed(2)}/>
              <StatCard title="Improvement" value={`${improvementOverBaseline.toFixed(2)}%`} trend={improvementOverBaseline>0?'up':'down'} trendValue={`${Math.abs(improvementOverBaseline).toFixed(2)}%`}/>
            </div>
          )}
        </Card>

        {/* Charts Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">

          {/* Revenue vs Baseline */}
          <Card className="lg:col-span-2 p-4">
            <h3 className="text-lg font-semibold mb-2">Revenue vs Baseline</h3>
            {rewardHistory.length>0 && baselineHistory.length>0
              ? <RevenueVsBaselineChart agentRewards={rewardHistory} baselineRewards={baselineHistory}/>
              : <div className="h-64 flex items-center justify-center text-gray-500">No training data</div>
            }
          </Card>

          {/* Price-Demand-Revenue */}
          <Card className="p-4">
            <h3 className="text-lg font-semibold mb-2">Price‑Demand‑Revenue</h3>
            {priceDemandRaw.length>0
              ? <>
                  <select
                    className="mb-4 p-2 border rounded"
                    value={selectedProductIdx}
                    onChange={e=>setSelectedProductIdx(Number(e.target.value))}
                  >
                    {priceDemandRaw.map((pd, idx)=>(
                      <option key={idx} value={idx}>{pd.product}</option>
                    ))}
                  </select>
                  <PriceDemandRevenueChart
                    data={priceDemandRaw[selectedProductIdx].pricePoints.map((p,i)=>({
                      price: p,
                      demand: priceDemandRaw[selectedProductIdx].demand[i],
                      revenue: priceDemandRaw[selectedProductIdx].revenue[i]
                    }))}
                  />
                </>
              : <div className="h-64 flex items-center justify-center text-gray-500">No data</div>
            }
          </Card>

          {/* Customer Segments */}
          <Card className="p-4">
            <h3 className="text-lg font-semibold mb-2">Customer Segments</h3>
            {customerSegments.length>0
              ? <CustomerSegmentPie data={customerSegments}/>
              : <div className="h-64 flex items-center justify-center text-gray-500">No data</div>
            }
          </Card>

          {/* Product Performance */}
          <Card className="p-4">
            <h3 className="text-lg font-semibold mb-2">Product Performance</h3>
            {products.length>0
              ? <ProductPerformanceChart products={products}/>
              : <div className="h-64 flex items-center justify-center text-gray-500">No data</div>
            }
          </Card>

          {/* Time‑Based Pricing */}
          <Card className="p-4">
            <h3 className="text-lg font-semibold mb-2">Time‑Based Pricing</h3>
            {timePricingRaw.timeOfDay.length>0
              ? <TimePricingChart
                  data={{
                    timeSlots: timePricingRaw.timeOfDay,
                    prices: timePricingRaw.priceMultipliers
                  }}
                />
              : <div className="h-64 flex items-center justify-center text-gray-500">No data</div>
            }
          </Card>

          {/* Customer Retention */}
          <Card className="p-4">
            <h3 className="text-lg font-semibold mb-2">Customer Retention</h3>
            <CustomerRetentionChart />
          </Card>

          {/* Price Sensitivity Heatmap */}
          <Card className="lg:col-span-2 p-4">
            <h3 className="text-lg font-semibold mb-2">Price Sensitivity Heatmap</h3>
            {products.length>0
              ? <PriceSensitivityHeatmap products={products}/>
              : <div className="h-64 flex items-center justify-center text-gray-500">No data</div>
            }
          </Card>

        </div>
      </div>
    </div>
  )
}

export default Dashboard
