// src/pages/Dashboard.tsx
import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Card from '../components/Card';
import StatCard from '../components/StatCard';
import RevenueVsBaselineChart from '../components/RevenueVsBaselineChart';
import PriceDemandRevenueChart from '../components/PriceDemandRevenueChart';
import CustomerSegmentPie from '../components/CustomerSegmentPie';
import ProductPerformanceChart from '../components/ProductPerformanceChart';
import TimePricingChart from '../components/TimePricingChart';
import CustomerRetentionChart from '../components/CustomerRetentionChart';
import PriceSensitivityHeatmap from '../components/PriceSensitivityHeatmap';
import Button from '../components/Button';
import LoadingSpinner from '../components/LoadingSpinner';
import apiClient from '../api/apiClient';

interface TrainingStatus {
  isTraining: boolean;
  currentEpisode: number;
  totalEpisodes: number;
}

interface TrainingResults {
  finalReward: number;
  avgLast10: number;
  improvementOverBaseline: number;
  rewardHistory: number[];
  baselineHistory: number[];
}

const Dashboard: React.FC = () => {
  const [products, setProducts] = useState<any[]>([]);
  const [customerSegments, setCustomerSegments] = useState<any[]>([]);
  const [priceDemandData, setPriceDemandData] = useState<any[]>([]);
  const [timePricingData, setTimePricingData] = useState<any>(null);

  const [trainingStatus, setTrainingStatus] = useState<TrainingStatus>({
    isTraining: false,
    currentEpisode: 0,
    totalEpisodes: 100,
  });
  const [trainingResults, setTrainingResults] = useState<TrainingResults>({
    finalReward: 0,
    avgLast10: 0,
    improvementOverBaseline: 0,
    rewardHistory: [],
    baselineHistory: []
  });

  const [trainingEpisodes, setTrainingEpisodes] = useState<number>(100);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  // Ensures whatever the API returns gets coerced into numbers/arrays
  const sanitizeResults = (raw: any): TrainingResults => ({
    finalReward: Number(raw.finalReward ?? 0),
    avgLast10: Number(raw.avgLast10 ?? 0),
    improvementOverBaseline: Number(raw.improvementOverBaseline ?? 0),
    rewardHistory: Array.isArray(raw.rewardHistory)
      ? raw.rewardHistory.map((x: any) => Number(x) || 0)
      : [],
    baselineHistory: Array.isArray(raw.baselineHistory)
      ? raw.baselineHistory.map((x: any) => Number(x) || 0)
      : []
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        setError(null);

        const [productsData, segmentsData, priceDemand, timePricing, statusData, rawResults] = await Promise.all([
          apiClient.getProducts(),
          apiClient.getCustomerSegments(),
          apiClient.getPriceDemandData(),
          apiClient.getTimePricingData(),
          apiClient.getTrainingStatus(),
          apiClient.getTrainingResults()
        ]);

        setProducts(productsData);
        setCustomerSegments(segmentsData);
        setPriceDemandData(priceDemand);
        setTimePricingData(timePricing);
        setTrainingStatus(statusData);
        setTrainingResults(sanitizeResults(rawResults));
      } catch (err) {
        console.error(err);
        setError('Failed to load dashboard data.');
      } finally {
        setLoading(false);
      }
    };

    fetchData();

    const interval = setInterval(async () => {
      try {
        const status = await apiClient.getTrainingStatus();
        setTrainingStatus(status);
        if (status.isTraining) {
          const raw = await apiClient.getTrainingResults();
          setTrainingResults(sanitizeResults(raw));
        }
      } catch (err) {
        console.error(err);
      }
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  const handleStartTraining = async () => {
    try {
      setError(null);
      await apiClient.startTraining({
        episodes: trainingEpisodes,
        useBaseline: true,
        baselineStrategy: 'combined'
      });
      const status = await apiClient.getTrainingStatus();
      setTrainingStatus(status);
    } catch (err) {
      console.error(err);
      setError('Failed to start training.');
    }
  };

  const trainingProgress =
    trainingStatus.isTraining && trainingStatus.totalEpisodes > 0
      ? Math.round(
          (trainingStatus.currentEpisode / trainingStatus.totalEpisodes) * 100
        )
      : 0;

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center">
        <LoadingSpinner size="large" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-100 flex items-center justify-center p-4">
        <Card className="max-w-lg w-full text-center p-6">
          <h2 className="text-xl font-semibold mb-2">Error</h2>
          <p className="mb-4">{error}</p>
          <Button onClick={() => window.location.reload()} variant="primary">
            Retry
          </Button>
        </Card>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-100">
      <Header title="Smart Dynamic Pricing Dashboard" />

      <div className="container mx-auto px-4 py-6">
        {/* AI Training Controls */}
        <Card className="mb-6 p-4">
          <h2 className="text-xl font-semibold mb-4">AI Training Controls</h2>
          {trainingStatus.isTraining ? (
            <>
              <div className="mb-4">
                <div className="flex justify-between mb-1">
                  <span>Progress</span>
                  <span>{trainingProgress}%</span>
                </div>
                <div className="w-full bg-gray-200 h-2.5 rounded">
                  <div
                    className="bg-blue-600 h-2.5 rounded transition-all"
                    style={{ width: `${trainingProgress}%` }}
                  />
                </div>
              </div>
              <div className="flex flex-wrap gap-4">
                <StatCard
                  title="Current Reward"
                  value={trainingResults.finalReward.toFixed(2)}
                  trend={trainingResults.finalReward > 0 ? 'up' : 'down'}
                  trendValue={Math.abs(trainingResults.finalReward).toFixed(2)}
                />
                <StatCard
                  title="Avg Last 10"
                  value={trainingResults.avgLast10.toFixed(2)}
                  trend={trainingResults.avgLast10 > 0 ? 'up' : 'down'}
                  trendValue={Math.abs(trainingResults.avgLast10).toFixed(2)}
                />
                <StatCard
                  title="Improvement"
                  value={`${trainingResults.improvementOverBaseline.toFixed(2)}%`}
                  trend={
                    trainingResults.improvementOverBaseline > 0
                      ? 'up'
                      : 'down'
                  }
                  trendValue={`${Math.abs(
                    trainingResults.improvementOverBaseline
                  ).toFixed(2)}%`}
                />
              </div>
            </>
          ) : (
            <div className="flex flex-col sm:flex-row gap-4 items-center">
              <div>
                <label htmlFor="episodes" className="block mb-1">
                  Episodes
                </label>
                <input
                  id="episodes"
                  type="number"
                  min={10}
                  max={1000}
                  value={trainingEpisodes}
                  onChange={(e) =>
                    setTrainingEpisodes(Number(e.target.value))
                  }
                  className="border p-2 rounded w-24"
                />
              </div>
              <Button onClick={handleStartTraining} variant="primary">
                Start Training
              </Button>
            </div>
          )}
        </Card>

        {/* Charts */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <Card className="lg:col-span-2 p-4">
            <h2 className="text-xl font-semibold mb-4">
              Revenue vs Baseline
            </h2>
            <RevenueVsBaselineChart
              agentRewards={trainingResults.rewardHistory}
              baselineRewards={trainingResults.baselineHistory}
            />
          </Card>

          <Card className="p-4">
            <h2 className="text-xl font-semibold mb-4">
              Price‑Demand‑Revenue
            </h2>
            <PriceDemandRevenueChart data={priceDemandData} />
