// src/pages/Dashboard.tsx
import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Card from '../components/Card';
import StatCard from '../components/StatCard';
import LineChart from '../components/LineChart';
import RevenueVsBaselineChart from '../components/RevenueVsBaselineChart';
import PriceDemandRevenueChart from '../components/PriceDemandRevenueChart';
import CustomerSegmentPie from '../components/CustomerSegmentPie';
import ProductPerformanceChart from '../components/ProductPerformanceChart';
import TimePricingChart from '../components/TimePricingChart';
import CustomerRetentionChart from '../components/CustomerRetentionChart';
import PriceSensitivityHeatmap from '../components/PriceSensitivityHeatmap';
import Button from '../components/Button';
import LoadingSpinner from '../components/LoadingSpinner';
import apiClient from '../api/apiClient';

interface TrainingStatus {
  isTraining: boolean;
  currentEpisode: number;
  totalEpisodes: number;
}

interface TrainingResults {
  finalReward: number;
  avgLast10: number;
  improvementOverBaseline: number;
  rewardHistory: number[];
  baselineHistory: number[];
}

const Dashboard: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Training controls
  const [trainingStatus, setTrainingStatus] = useState<TrainingStatus>({ isTraining: false, currentEpisode: 0, totalEpisodes: 0 });
  const [trainingResults, setTrainingResults] = useState<TrainingResults>({ finalReward: 0, avgLast10: 0, improvementOverBaseline: 0, rewardHistory: [], baselineHistory: [] });
  const [episodes, setEpisodes] = useState(10);

  // Domain data
  const [products, setProducts] = useState<any[]>([]);
  const [segments, setSegments] = useState<any[]>([]);
  const [priceDemand, setPriceDemand] = useState<any[]>([]);
  const [timePricing, setTimePricing] = useState<{timeOfDay: string[]; priceMultipliers: number[]}>({ timeOfDay: [], priceMultipliers: [] });

  // Load initial
  useEffect(() => {
    const init = async () => {
      try {
        setLoading(true);
        setError(null);
        const [prod, segRaw, pdRaw, tpRaw, statusRaw, resultsRaw] = await Promise.all([
          apiClient.getProducts(),
          apiClient.getCustomerSegments(),
          apiClient.getPriceDemandData(),
          apiClient.getTimePricingData(),
          apiClient.getTrainingStatus(),
          apiClient.getTrainingResults(),
        ]);
        setProducts(prod || []);
        setSegments(segRaw || []);
        setPriceDemand(pdRaw || []);
        setTimePricing(tpRaw || { timeOfDay: [], priceMultipliers: [] });
        setTrainingStatus(statusRaw || { isTraining: false, currentEpisode: 0, totalEpisodes: 0 });
        setTrainingResults(resultsRaw || { finalReward: 0, avgLast10: 0, improvementOverBaseline: 0, rewardHistory: [], baselineHistory: [] });
      } catch (e) {
        console.error(e);
        setError('Failed to load data.');
      } finally {
        setLoading(false);
      }
    };
    init();

    const poll = setInterval(async () => {
      try {
        const statusRaw = await apiClient.getTrainingStatus();
        setTrainingStatus(statusRaw);
        if (statusRaw.isTraining) {
          const resultsRaw = await apiClient.getTrainingResults();
          setTrainingResults(resultsRaw);
        }
      } catch {};
    }, 2000);
    return () => clearInterval(poll);
  }, []);

  const startTraining = async () => {
    try {
      setError(null);
      await apiClient.startTraining({ episodes, useBaseline: true, baselineStrategy: 'combined' });
      const statusRaw = await apiClient.getTrainingStatus();
      setTrainingStatus(statusRaw);
    } catch (e) {
      console.error(e);
      setError('Failed to start training.');
    }
  };

  if (loading) return <div className="min-h-screen flex items-center justify-center"><LoadingSpinner size="large"/></div>;
  if (error) return <div className="min-h-screen flex items-center justify-center"><p className="text-red-500">{error}</p></div>;

  const progress = trainingStatus.totalEpisodes > 0 ? (trainingStatus.currentEpisode / trainingStatus.totalEpisodes) * 100 : 0;

  return (
    <div className="min-h-screen bg-gray-100">
      <Header title="Smart Pricing Dashboard" />
      <div className="container mx-auto p-4 space-y-6">

        {/* Training Section */}
        <Card className="space-y-4">
          <h2 className="text-2xl font-bold">Agent Training</h2>
          {!trainingStatus.isTraining ? (
            <div className="flex items-center space-x-2">
              <input type="number" min={1} max={1000} value={episodes} onChange={e => setEpisodes(+e.target.value)} className="w-20 p-2 border rounded" />
              <Button variant="primary" onClick={startTraining}>Start {episodes} Episodes</Button>
            </div>
          ) : (
            <div>
              <div className="h-4 bg-gray-300 rounded overflow-hidden">
                <div className="h-full bg-green-500" style={{ width: `${progress}%` }} />
              </div>
              <p className="mt-2">Progress: {trainingStatus.currentEpisode}/{trainingStatus.totalEpisodes} ({progress.toFixed(0)}%)</p>
            </div>
          )}

          {/* Live Stats */}
          <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
            <StatCard title="Final Reward" value={trainingResults.finalReward.toFixed(2)} change={trainingResults.finalReward} />
            <StatCard title="Avg Last 10" value={trainingResults.avgLast10.toFixed(2)} change={trainingResults.avgLast10} />
            <StatCard title="Improvement" value={`${trainingResults.improvementOverBaseline.toFixed(2)}%`} change={trainingResults.improvementOverBaseline} />
          </div>

          {/* Reward History Chart */}
          {trainingResults.rewardHistory.length > 0 && (
            <div className="h-64">
              <LineChart data={{
                labels: trainingResults.rewardHistory.map((_, i) => `Ep ${i+1}`),
                datasets: [{ label: 'Reward', data: trainingResults.rewardHistory, borderColor: '#2563EB', backgroundColor: 'rgba(37,99,235,0.2)', borderWidth: 2, tension: 0.4 }]
              }} />
            </div>
          )}
        </Card>

        {/* Dashboard Grid */}
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">

          {/* Revenue vs Baseline */}
          <Card>
            <h3 className="text-xl font-semibold mb-2">Revenue vs Baseline</h3>
            <RevenueVsBaselineChart agentRewards={trainingResults.rewardHistory} baselineRewards={trainingResults.baselineHistory} />
          </Card>

          {/* Price Demand Revenue */}
          <Card>
            <h3 className="text-xl font-semibold mb-2">Price‑Demand‑Revenue</h3>
            <PriceDemandRevenueChart data={priceDemand} />
          </Card>

          {/* Customer Segments */}
          <Card>
            <h3 className="text-xl font-semibold mb-2">Customer Segments</h3>
            <CustomerSegmentPie data={segments.map(s => ({ name: s.name, percentage: s.size*100 }))} />
          </Card>

          {/* Product Performance */}
          <Card>
            <h3 className="text-xl font-semibold mb-2">Product Performance</h3>
            <ProductPerformanceChart products={products} />
          </Card>

          {/* Time-Based Pricing */}
          <Card>
            <h3 className="text-xl font-semibold mb-2">Time‑Pricing</h3>
            <TimePricingChart data={{ timeSlots: timePricing.timeOfDay, prices: timePricing.priceMultipliers }} />
          </Card>

          {/* Retention */}
          <Card>
            <h3 className="text-xl font-semibold mb-2">Customer Retention</h3>
            <CustomerRetentionChart />
          </Card>

          {/* Price Sensitivity Heatmap */}
          <Card className="lg:col-span-2">
            <h3 className="text-xl font-semibold mb-2">Price Sensitivity</h3>
            <PriceSensitivityHeatmap products={products} />
          </Card>

        </div>
      </div>
    </div>
  );
};

export default Dashboard;
