import React, { useState, useEffect } from 'react';

type TimePricingChartProps = {
  data?: any;
};

const TimePricingChart: React.FC<TimePricingChartProps> = ({ data }) => {
  const [chartData, setChartData] = useState<{
    labels: string[];
    datasets: {
      label: string;
      data: number[];
      borderColor: string;
      backgroundColor: string;
      borderWidth: number;
      tension: number;
    }[];
  }>({
    labels: [],
    datasets: []
  });

  useEffect(() => {
    if (data && data.timeSlots && data.prices) {
      setChartData({
        labels: data.timeSlots,
        datasets: [
          {
            label: 'Optimal Price',
            data: data.prices,
            borderColor: 'rgba(153, 102, 255, 1)',
            backgroundColor: 'rgba(153, 102, 255, 0.2)',
            borderWidth: 2,
            tension: 0.4,
          }
        ]
      });
    } else {
      // Generate sample data if none provided
      const timeSlots = [
        '8:00 AM', '10:00 AM', '12:00 PM', '2:00 PM', 
        '4:00 PM', '6:00 PM', '8:00 PM', '10:00 PM'
      ];

      // Generate prices with a pattern (higher during peak hours)
      const prices = [
        85, 90, 110, 105, 100, 120, 115, 95
      ];

      setChartData({
        labels: timeSlots,
        datasets: [
          {
            label: 'Optimal Price',
            data: prices,
            borderColor: 'rgba(153, 102, 255, 1)',
            backgroundColor: 'rgba(153, 102, 255, 0.2)',
            borderWidth: 2,
            tension: 0.4,
          }
        ]
      });
    }
  }, [data]);

  // In a real implementation, we would use Chart.js
  // For this example, we'll create a simplified visual representation
  return (
    <div className="h-full relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-r from-purple-50 to-indigo-50 opacity-50 rounded-lg"></div>
      <div className="relative h-full flex flex-col">
        <div className="flex justify-between items-center px-4 py-2">
          <div className="flex flex-wrap gap-2">
            {chartData.datasets.map((dataset, index) => (
              <div key={index} className="flex items-center">
                <div
                  className="w-3 h-3 rounded-full mr-1"
                  style={{ backgroundColor: dataset.borderColor }}
                ></div>
                <span className="text-xs font-medium">{dataset.label}</span>
              </div>
            ))}
          </div>
        </div>
        <div className="flex-1 p-4">
          <div className="h-full flex items-end">
            {chartData.labels.map((label, labelIndex) => {
              const value = chartData.datasets[0]?.data[labelIndex] || 0;
              const maxValue = Math.max(...(chartData.datasets[0]?.data || [1]));
              const minValue = Math.min(...(chartData.datasets[0]?.data || [0]));
              const range = maxValue - minValue;
              // Scale to fit in the chart area (20-100%)
              const height = range > 0 ? ((value - minValue) / range) * 80 + 20 : 50;

              return (
                <div key={labelIndex} className="flex-1 flex flex-col items-center">
                  <div className="w-full flex justify-center items-end h-[85%] relative">
                    {labelIndex > 0 && (
                      <div 
                        className="absolute bottom-0 w-full h-full"
                        style={{
                          borderTop: '2px dashed rgba(153, 102, 255, 0.5)',
                          borderTopWidth: '2px',
                          height: `${height}%`,
                          zIndex: 0
                        }}
                      ></div>
                    )}
                    <div 
                      className="w-4 h-4 rounded-full bg-purple-500 z-10 mb-[-8px]"
                      style={{
                        bottom: `${height}%`,
                        position: 'absolute',
                        transform: 'scale(1)',
                        animation: `appear 1s ease-out ${labelIndex * 0.1}s forwards`
                      }}
                    >
                      <div className="absolute top-[-25px] left-1/2 transform -translate-x-1/2 bg-purple-100 text-purple-800 text-xs font-medium px-2 py-0.5 rounded">
                        ${value}
                      </div>
                    </div>
                  </div>
                  <div className="text-xs text-gray-500 mt-1 truncate w-full text-center">
                    {label}
                  </div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
      <style jsx>{`
        @keyframes appear {
          from { transform: scale(0); opacity: 0; }
          to { transform: scale(1); opacity: 1; }
        }
      `}</style>
    </div>
  );
};

export default TimePricingChart;
