import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Card from '../components/Card';
import StatCard from '../components/StatCard';
import RevenueVsBaselineChart from '../components/RevenueVsBaselineChart';
import PriceDemandRevenueChart from '../components/PriceDemandRevenueChart';
import CustomerSegmentPie from '../components/CustomerSegmentPie';
import ProductPerformanceChart from '../components/ProductPerformanceChart';
import TimePricingChart from '../components/TimePricingChart';
import CustomerRetentionChart from '../components/CustomerRetentionChart';
import PriceSensitivityHeatmap from '../components/PriceSensitivityHeatmap';
import Button from '../components/Button';
import LoadingSpinner from '../components/LoadingSpinner';
import apiClient from '../api/apiClient';

// Types
interface TrainingStatus {
  isTraining: boolean;
  currentEpisode: number;
  totalEpisodes: number;
}

interface TrainingResults {
  finalReward: number;
  avgLast10: number;
  improvementOverBaseline: number;
  rewardHistory: number[];
  baselineHistory: number[];
}

interface Product {
  id: number;
  name: string;
  base_price: number;
  current_price: number;
  // ...other fields
}

interface PriceDemandData {
  product: string;
  pricePoints: number[];
  demand: number[];
  revenue: number[];
}

const Dashboard: React.FC = () => {
  const [products, setProducts] = useState<Product[]>([]);
  const [customerSegments, setCustomerSegments] = useState<{ name: string; size: number }[]>([]);
  const [priceDemandAll, setPriceDemandAll] = useState<PriceDemandData[]>([]);
  const [timePricing, setTimePricing] = useState<{ timeOfDay: string[]; priceMultipliers: number[] }>({ timeOfDay: [], priceMultipliers: [] });
  const [trainingStatus, setTrainingStatus] = useState<TrainingStatus>({ isTraining: false, currentEpisode: 0, totalEpisodes: 0 });
  const [trainingResults, setTrainingResults] = useState<TrainingResults>({ finalReward: 0, avgLast10: 0, improvementOverBaseline: 0, rewardHistory: [], baselineHistory: [] });
  const [trainingEpisodes, setTrainingEpisodes] = useState<number>(100);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedPdIndex, setSelectedPdIndex] = useState<number>(0);

  const sanitizeResults = (raw: any): TrainingResults => ({
    finalReward: Number(raw?.finalReward) || 0,
    avgLast10: Number(raw?.avgLast10) || 0,
    improvementOverBaseline: Number(raw?.improvementOverBaseline) || 0,
    rewardHistory: Array.isArray(raw?.rewardHistory) ? raw.rewardHistory.map((v: any) => Number(v) || 0) : [],
    baselineHistory: Array.isArray(raw?.baselineHistory) ? raw.baselineHistory.map((v: any) => Number(v) || 0) : []
  });

  useEffect(() => {
    let poll: NodeJS.Timeout;
    const loadDashboard = async () => {
      try {
        setLoading(true);
        setError(null);
        const [ prodRes, segRes, pdRes, tpRes, statusRes, resultsRes ] = await Promise.all([
          apiClient.getProducts(),
          apiClient.getCustomerSegments(),
          apiClient.getPriceDemandData(),
          apiClient.getTimePricingData(),
          apiClient.getTrainingStatus(),
          apiClient.getTrainingResults()
        ]);

        setProducts(Array.isArray(prodRes) ? prodRes : []);
        setCustomerSegments(Array.isArray(segRes) ? segRes.map((s: any) => ({ name: s.name, size: s.size })) : []);
        setPriceDemandAll(Array.isArray(pdRes) ? pdRes : []);
        setTimePricing({ timeOfDay: tpRes.timeOfDay || [], priceMultipliers: tpRes.priceMultipliers || [] });
        setTrainingStatus(statusRes || { isTraining: false, currentEpisode: 0, totalEpisodes: 0 });
        setTrainingResults(sanitizeResults(resultsRes));
      } catch (e) {
        console.error(e);
        setError('Failed to load dashboard');
      } finally {
        setLoading(false);
      }
    };

    loadDashboard();
    poll = setInterval(async () => {
      try {
        const status = await apiClient.getTrainingStatus();
        setTrainingStatus(status);
        if (status.isTraining) {
          const results = await apiClient.getTrainingResults();
          setTrainingResults(sanitizeResults(results));
        }
      } catch {}
    }, 2000);

    return () => clearInterval(poll);
  }, []);

  const handleStart = async () => {
    try {
      setError(null);
      await apiClient.startTraining({ episodes: trainingEpisodes, useBaseline: true, baselineStrategy: 'combined' });
      const status = await apiClient.getTrainingStatus();
      setTrainingStatus(status);
    } catch (e) {
      console.error(e);
      setError('Failed to start training');
    }
  };

  if (loading) return <div className="flex items-center justify-center h-screen"><LoadingSpinner size="large" /></div>;
  if (error) return <div className="p-6 text-center"><p className="text-red-600">{error}</p><Button onClick={() => window.location.reload()}>Retry</Button></div>;

  const { finalReward, avgLast10, improvementOverBaseline, rewardHistory, baselineHistory } = trainingResults;
  const progressPct = trainingStatus.isTraining ? Math.round((trainingStatus.currentEpisode / trainingStatus.totalEpisodes)*100) : 0;
  const selectedPd = priceDemandAll[selectedPdIndex] || { pricePoints: [], demand: [], revenue: [], product: '' };

  return (
    <div className="min-h-screen bg-gray-50">
      <Header title="Smart Dynamic Pricing Dashboard" />
      <div className="container mx-auto p-4 space-y-6">

        <Card>
          <div className="flex justify-between items-center">
            <div>
              <h2 className="text-lg font-semibold">AI Training</h2>
              <p className="text-sm text-gray-600">
                {trainingStatus.isTraining ? `Episode ${trainingStatus.currentEpisode} / ${trainingStatus.totalEpisodes} (${progressPct}%)` : 'Not training'}
              </p>
            </div>
            {!trainingStatus.isTraining && (
              <div className="flex items-center gap-2">
                <input type="number" min={10} max={1000} value={trainingEpisodes} onChange={e=>setTrainingEpisodes(+e.target.value)} className="w-20 p-2 border" />
                <Button onClick={handleStart}>Start</Button>
              </div>
            )}
          </div>
          {trainingStatus.isTraining && <div className="h-2 bg-gray-200 rounded overflow-hidden mt-2"><div className="h-full bg-blue-600" style={{ width: `${progressPct}%` }} /></div>}
          {trainingStatus.isTraining && (
            <div className="grid grid-cols-3 gap-4 mt-4">
              <StatCard title="Final Reward" value={finalReward.toFixed(2)} />
              <StatCard title="Avg Last 10" value={avgLast10.toFixed(2)} />
              <StatCard title="Improvement" value={`${improvementOverBaseline.toFixed(2)}%`} />
            </div>
          )}
        </Card>

        <Card>
          <h3 className="text-lg font-semibold mb-2">Revenue vs Baseline</h3>
          {rewardHistory.length && baselineHistory.length ? (
            <RevenueVsBaselineChart agentRewards={rewardHistory} baselineRewards={baselineHistory} />
          ) : <p className="text-gray-500">No data</p>}
        </Card>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">

          <Card>
            <h3 className="text-lg font-semibold mb-2">Price‑Demand‑Revenue</h3>
            {priceDemandAll.length > 0 ? (
              <>
                <select value={selectedPdIndex} onChange={e=>setSelectedPdIndex(+e.target.value)} className="mb-2 p-1 border">
                  {priceDemandAll.map((pd, i)=><option key={i} value={i}>{pd.product}</option>)}
                </select>
                <PriceDemandRevenueChart data={selectedPd} />
              </>
            ) : <p className="text-gray-500">No data</p>}
          </Card>

          <Card>
            <h3 className="text-lg font-semibold mb-2">Customer Segments</h3>
            <CustomerSegmentPie data={customerSegments.map(s=>({ name: s.name, value: s.size*100 }))} />
          </Card>

          <Card>
            <h3 className="text-lg font-semibold mb-2">Product Performance</h3>
            <ProductPerformanceChart products={products} />
          </Card>

          <Card>
            <h3 className="text-lg font-semibold mb-2">Time‑Based Pricing</h3>
            <TimePricingChart data={timePricing} />
          </Card>

          <Card>
            <h3 className="text-lg font-semibold mb-2">Customer Retention</h3>
            <CustomerRetentionChart />
