// src/pages/Dashboard.tsx
import React, { useState, useEffect } from 'react'
import Header from '../components/Header'
import Card from '../components/Card'
import StatCard from '../components/StatCard'
import RevenueVsBaselineChart from '../components/RevenueVsBaselineChart'
import PriceDemandRevenueChart from '../components/PriceDemandRevenueChart'
import CustomerSegmentPie from '../components/CustomerSegmentPie'
import ProductPerformanceChart from '../components/ProductPerformanceChart'
import TimePricingChart from '../components/TimePricingChart'
import CustomerRetentionChart from '../components/CustomerRetentionChart'
import PriceSensitivityHeatmap from '../components/PriceSensitivityHeatmap'
import Button from '../components/Button'
import LoadingSpinner from '../components/LoadingSpinner'
import apiClient from '../api/apiClient'

interface TrainingStatus {
  isTraining: boolean
  currentEpisode: number
  totalEpisodes: number
}
interface TrainingResults {
  finalReward: number
  avgLast10: number
  improvementOverBaseline: number
  rewardHistory: number[]
  baselineHistory: number[]
}
interface Segment { name: string; percentage: number }
interface PDPoint { price: number; demand: number }
interface TimePricing { timeOfDay: string[]; priceMultipliers: number[] }
interface HeatmapProduct { name: string; base_price?: number }
interface FullProduct extends HeatmapProduct { current_price: number; stock: number }

export default function Dashboard() {
  // State
  const [products, setProducts] = useState<FullProduct[]>([])
  const [pieData, setPieData] = useState<Segment[]>([])
  const [pdData, setPdData] = useState<PDPoint[]>([])
  const [timePricing, setTimePricing] = useState<TimePricing>({
    timeOfDay: [],
    priceMultipliers: []
  })
  const [trainingStatus, setTrainingStatus] = useState<TrainingStatus>({
    isTraining: false,
    currentEpisode: 0,
    totalEpisodes: 0
  })
  const [trainingResults, setTrainingResults] = useState<TrainingResults>({
    finalReward: 0,
    avgLast10: 0,
    improvementOverBaseline: 0,
    rewardHistory: [],
    baselineHistory: []
  })
  const [episodes, setEpisodes] = useState(100)
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState<string | null>(null)

  // Helpers
  const sanitize = (raw: any): TrainingResults => ({
    finalReward: Number(raw.finalReward) || 0,
    avgLast10: Number(raw.avgLast10) || 0,
    improvementOverBaseline: Number(raw.improvementOverBaseline) || 0,
    rewardHistory: Array.isArray(raw.rewardHistory)
      ? raw.rewardHistory.map((x: any) => Number(x) || 0)
      : [],
    baselineHistory: Array.isArray(raw.baselineHistory)
      ? raw.baselineHistory.map((x: any) => Number(x) || 0)
      : []
  })

  // Initial load + polling
  useEffect(() => {
    let poll: NodeJS.Timeout

    const loadAll = async () => {
      try {
        setLoading(true)
        setError(null)
        const [
          prodRaw,
          segRaw,
          pdRaw,
          tpRaw,
          statusRaw,
          resultsRaw
        ] = await Promise.all([
          apiClient.getProducts(),
          apiClient.getCustomerSegmentData(),
          apiClient.getPriceDemandData(),
          apiClient.getTimePricingData(),
          apiClient.getTrainingStatus(),
          apiClient.getTrainingResults()
        ])

        // products
        setProducts(Array.isArray(prodRaw) ? prodRaw : [])

        // pieData
        if (Array.isArray(segRaw)) {
          setPieData(
            segRaw.map((s: any) => ({
              name: s.name,
              percentage: Number(s.value) || 0
            }))
          )
        }

        // price-demand (first product only)
        if (Array.isArray(pdRaw) && pdRaw.length > 0) {
          const first = pdRaw[0]
          setPdData(
            first.pricePoints.map((p: number, i: number) => ({
              price: p,
              demand: first.demand[i]
            }))
          )
        }

        // time pricing
        setTimePricing({
          timeOfDay: Array.isArray(tpRaw.timeOfDay) ? tpRaw.timeOfDay : [],
          priceMultipliers: Array.isArray(tpRaw.priceMultipliers)
            ? tpRaw.priceMultipliers
            : []
        })

        // training
        setTrainingStatus(statusRaw)
        setTrainingResults(sanitize(resultsRaw))
      } catch (err) {
        console.error(err)
        setError('Failed to load dashboard data.')
      } finally {
        setLoading(false)
      }
    }

    loadAll()

    poll = setInterval(async () => {
      try {
        const statusRaw = await apiClient.getTrainingStatus()
        setTrainingStatus(statusRaw)
        if (statusRaw.isTraining) {
          const resultsRaw = await apiClient.getTrainingResults()
          setTrainingResults(sanitize(resultsRaw))
        }
      } catch {
        /* ignore */
      }
    }, 2000)

    return () => clearInterval(poll)
  }, [])

  // Start training
  const startTraining = async () => {
    try {
      setError(null)
      await apiClient.startTraining({
        episodes,
        useBaseline: true,
        baselineStrategy: 'combined'
      })
      const s = await apiClient.getTrainingStatus()
      setTrainingStatus(s)
    } catch {
      setError('Failed to start training.')
    }
  }

  // Loading / error UI
  if (loading)
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <LoadingSpinner size="large" />
      </div>
    )
  if (error)
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50 p-4">
        <Card className="max-w-md w-full text-center p-6">
          <h2 className="text-xl font-semibold">Error</h2>
          <p className="mt-2">{error}</p>
          <Button onClick={() => window.location.reload()} variant="primary">
            Retry
          </Button>
        </Card>
      </div>
    )

  const {
    finalReward,
    avgLast10,
    improvementOverBaseline,
    rewardHistory,
    baselineHistory
  } = trainingResults

  const progressPct =
    trainingStatus.isTraining && trainingStatus.totalEpisodes > 0
      ? Math.round(
          (trainingStatus.currentEpisode / trainingStatus.totalEpisodes) * 100
        )
      : 0

  return (
    <div className="min-h-screen bg-gray-50">
      <Header title="Smart Dynamic Pricing Dashboard" />

      <div className="container mx-auto px-4 py-6 space-y-6">
        {/* Training panel */}
        <Card>
          <div className="flex flex-col md:flex-row md:items-center md:justify-between">
            <div>
              <h2 className="text-lg font-semibold">AI Training</h2>
              <p className="text-sm text-gray-600">
                {trainingStatus.isTraining
                  ? `Episode ${trainingStatus.currentEpisode} of ${trainingStatus.totalEpisodes} (${progressPct}%)`
                  : 'Not training'}
              </p>
            </div>
            {!trainingStatus.isTraining && (
              <div className="mt-4 md:mt-0 flex items-center gap-2">
                <input
                  type="number"
                  min={10}
                  max={1000}
                  value={episodes}
                  onChange={(e) => setEpisodes(Number(e.target.value))}
                  className="w-20 p-2 border rounded"
                />
                <Button onClick={startTraining} variant="primary">
                  Start
                </Button>
              </div>
            )}
          </div>

          {trainingStatus.isTraining && (
            <>
              <div className="mt-4 h-2 w-full bg-gray-200 rounded overflow-hidden">
                <div
                  className="h-full bg-blue-600"
                  style={{ width: `${progressPct}%` }}
                />
              </div>
              <div className="grid grid-cols-1 sm:grid-cols-3 gap-4 mt-4">
                <StatCard
                  title="Final Reward"
                  value={finalReward.toFixed(2)}
                  trend={finalReward >= 0 ? 'up' : 'down'}
                  trendValue={Math.abs(finalReward).toFixed(2)}
                />
                <StatCard
                  title="Avg Last 10"
                  value={avgLast10.toFixed(2)}
                  trend={avgLast10 >= 0 ? 'up' : 'down'}
                  trendValue={Math.abs(avgLast10).toFixed(2)}
                />
                <StatCard
                  title="Improvement"
                  value={`${improvementOverBaseline.toFixed(2)}%`}
                  trend={improvementOverBaseline >= 0 ? 'up' : 'down'}
                  trendValue={`${Math.abs(improvementOverBaseline).toFixed(2)}%`}
                />
              </div>
            </>
          )}
        </Card>

        {/* Charts */}
        <div className="grid gap-6 lg:grid-cols-2">
          <Card className="lg:col-span-2">
            <h3 className="text-lg font-semibold mb-2">
              Cumulative Revenue vs Baseline
            </h3>
            <RevenueVsBaselineChart
              agentRewards={rewardHistory}
              baselineRewards={baselineHistory}
            />
          </Card>

          <Card>
            <h3 className="text-lg font-semibold mb-2">
              Price–Demand (per Price Point)
            </h3>
            <PriceDemandRevenueChart data={pdData} />
          </Card>

          <Card>
            <h3 className="text-lg font-semibold mb-2">Customer Segments</h3>
            {/* cast to any so TS is happy */}
            <CustomerSegmentPie data={pieData as any} />
          </Card>

          <Card>
            <h3 className="text-lg font-semibold mb-2">
              Product Performance
            </h3>
            {/* pass the full product list; your component can compute revenue internally */}
            <ProductPerformanceChart products={products as any} />
          </Card>

          <Card>
            <h3 className="text-lg font-semibold mb-2">
              Time-Based Optimal Prices
            </h3>
            <TimePricingChart data={timePricing} />
          </Card>

          <Card>
            <h3 className="text-lg font-semibold mb-2">
              Retention Over Episodes
            </h3>
            <CustomerRetentionChart
              data={{
                months: rewardHistory.map((_, i) => `Ep ${i + 1}`),
                retention: rewardHistory
              }}
            />
          </Card>

          <Card className="lg:col-span-2">
            <h3 className="text-lg font-semibold mb-2">
              Price Sensitivity Heatmap
            </h3>
            <PriceSensitivityHeatmap products={products as any} />
          </Card>
        </div>
      </div>
    </div>
  )
}
