// src/components/ProductPerformanceChart.tsx
import React from 'react';

type Product = {
  name: string;
  current_price: number;
  stock: number;
};

type ProductPerformanceChartProps = {
  products?: Product[];
};

const ProductPerformanceChart: React.FC<ProductPerformanceChartProps> = ({ products }) => {
  // Placeholder if no data
  if (!Array.isArray(products) || products.length === 0) {
    return (
      <div className="h-full flex items-center justify-center bg-gray-100 text-gray-500 rounded-lg">
        No data available
      </div>
    );
  }

  // Labels and projected revenues
  const labels = products.map(p => p.name);
  const values = products.map(p => p.current_price * p.stock);
  const maxVal = Math.max(...values, 1);

  return (
    <div className="h-full relative overflow-hidden rounded-lg bg-white shadow">
      {/* Soft gradient backdrop */}
      <div className="absolute inset-0 bg-gradient-to-r from-green-50 to-teal-50 opacity-50 rounded-lg"></div>
      <div className="relative h-full flex flex-col">
        <div className="px-4 py-2 border-b border-gray-200">
          <h3 className="text-sm font-medium text-gray-800">Projected Revenue</h3>
        </div>
        <div className="flex-1 p-4 flex items-end space-x-2">
          {labels.map((lbl, i) => {
            const v = values[i];
            const pct = (v / maxVal) * 100;
            return (
              <div key={i} className="flex-1 flex flex-col items-center">
                <div
                  className="w-full bg-teal-600 rounded-t-lg transition-all duration-500 ease-out"
                  style={{ height: `${pct}%` }}
                  title={`$${v.toFixed(2)}`}
                >
                  <div className="text-xs text-white text-center pt-1">
                    ${v.toFixed(0)}
                  </div>
                </div>
                <div className="mt-1 text-xs text-gray-600 truncate w-full text-center">
                  {lbl}
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default ProductPerformanceChart;
