import React, { useState, useEffect } from 'react';

type DataPoint = {
  x: number;
  y: number;
  product: string;
};

type PriceSensitivityHeatmapProps = {
  products?: any[];
};

const PriceSensitivityHeatmap: React.FC<PriceSensitivityHeatmapProps> = ({ products }) => {
  const [data, setData] = useState<{ dataPoints: DataPoint[] }>({ dataPoints: [] });
  const [loading, setLoading] = useState<boolean>(true);

  useEffect(() => {
    // Generate data points based on products
    if (products && products.length > 0) {
      const dataPoints: DataPoint[] = [];

      products.forEach(product => {
        const basePrice = product.base_price || product.basePrice || 100;

        // Generate price points (70% to 130% of base price)
        for (let pricePercent = 70; pricePercent <= 130; pricePercent += 10) {
          const price = basePrice * (pricePercent / 100);

          // Calculate demand based on price (inverse relationship)
          // Higher price = lower demand
          const baseDemand = 100 * (100 / pricePercent) ** 1.5;

          // Add some randomness
          const randomFactor = 0.9 + Math.random() * 0.2; // Random factor between 0.9 and 1.1

          dataPoints.push({
            x: price,
            y: baseDemand * randomFactor,
            product: product.name
          });
        }
      });

      setData({ dataPoints });
    } else {
      // Generate default data if no products are provided
      const defaultDataPoints: DataPoint[] = [];

      // Generate sample data points
      for (let price = 70; price <= 130; price += 10) {
        // Calculate demand based on price (inverse relationship)
        const demand = 100 * (100 / price) ** 1.5;

        // Add some randomness
        const randomFactor = 0.9 + Math.random() * 0.2;

        defaultDataPoints.push({
          x: price,
          y: demand * randomFactor,
          product: 'Sample Product'
        });
      }

      setData({ dataPoints: defaultDataPoints });
    }

    setLoading(false);
  }, [products]);

  if (loading) {
    return (
      <div className="flex items-center justify-center h-full">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  // If no data is available
  if (!data.dataPoints || data.dataPoints.length === 0) {
    return (
      <div className="flex items-center justify-center h-full bg-gray-50 rounded-lg">
        <div className="text-center p-6">
          <svg
            className="mx-auto h-12 w-12 text-gray-400"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
            aria-hidden="true"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M9 19v-6a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2a2 2 0 002-2zm0 0V9a2 2 0 012-2h2a2 2 0 012 2v10m-6 0a2 2 0 002 2h2a2 2 0 002-2m0 0V5a2 2 0 012-2h2a2 2 0 012 2v14a2 2 0 01-2 2h-2a2 2 0 01-2-2z"
            />
          </svg>
          <h3 className="mt-2 text-sm font-medium text-gray-900">No data available</h3>
          <p className="mt-1 text-sm text-gray-500">Data will appear here once generated.</p>
        </div>
      </div>
    );
  }

  // Find min and max values for x and y
  const xValues = data.dataPoints.map(point => point.x);
  const yValues = data.dataPoints.map(point => point.y);
  const minX = Math.min(...xValues);
  const maxX = Math.max(...xValues);
  const minY = Math.min(...yValues);
  const maxY = Math.max(...yValues);

  // Normalize data points to fit in the chart area
  const normalizedDataPoints = data.dataPoints.map(point => ({
    x: ((point.x - minX) / (maxX - minX)) * 100,
    y: ((point.y - minY) / (maxY - minY)) * 100,
    product: point.product,
    originalX: point.x,
    originalY: point.y
  }));

  // Group data points by product
  const productGroups: { [key: string]: typeof normalizedDataPoints } = {};
  normalizedDataPoints.forEach(point => {
    if (!productGroups[point.product]) {
      productGroups[point.product] = [];
    }
    productGroups[point.product].push(point);
  });

  // Generate colors for each product
  const productColors: { [key: string]: string } = {};
  Object.keys(productGroups).forEach((product, index) => {
    const hue = (index * 137) % 360; // Golden angle approximation for good color distribution
    productColors[product] = `hsl(${hue}, 70%, 60%)`;
  });

  return (
    <div className="h-full relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-r from-yellow-50 to-orange-50 opacity-50 rounded-lg"></div>
      <div className="relative h-full p-4">
        <div className="flex mb-2">
          {Object.keys(productGroups).map((product, index) => (
            <div key={index} className="flex items-center mr-4">
              <div
                className="w-3 h-3 rounded-full mr-1"
                style={{ backgroundColor: productColors[product] }}
              ></div>
              <span className="text-xs font-medium">{product}</span>
            </div>
          ))}
        </div>

        <div className="h-[calc(100%-2rem)] relative border-b border-l border-gray-300">
          {/* Y-axis label */}
          <div className="absolute -left-6 top-1/2 -rotate-90 text-xs text-gray-500">Demand</div>

          {/* X-axis label */}
          <div className="absolute bottom-[-1.5rem] left-1/2 transform -translate-x-1/2 text-xs text-gray-500">Price</div>

          {/* Y-axis ticks */}
          <div className="absolute left-[-0.5rem] top-0 h-full flex flex-col justify-between">
            <span className="text-[0.6rem] text-gray-500">{Math.round(maxY)}</span>
            <span className="text-[0.6rem] text-gray-500">{Math.round((maxY + minY) / 2)}</span>
            <span className="text-[0.6rem] text-gray-500">{Math.round(minY)}</span>
          </div>

          {/* X-axis ticks */}
          <div className="absolute bottom-[-1rem] left-0 w-full flex justify-between">
            <span className="text-[0.6rem] text-gray-500">{Math.round(minX)}</span>
            <span className="text-[0.6rem] text-gray-500">{Math.round((maxX + minX) / 2)}</span>
            <span className="text-[0.6rem] text-gray-500">{Math.round(maxX)}</span>
          </div>

          {/* Data points */}
          {Object.entries(productGroups).map(([product, points], groupIndex) => (
            <React.Fragment key={groupIndex}>
              {points.map((point, pointIndex) => (
                <div
                  key={`${groupIndex}-${pointIndex}`}
                  className="absolute w-4 h-4 rounded-full -ml-2 -mt-2 transition-all duration-500 ease-out"
                  style={{
                    left: `${point.x}%`,
                    bottom: `${point.y}%`,
                    backgroundColor: productColors[product],
                    opacity: 0.7,
                    transform: 'scale(1)',
                    animation: `appear 1s ease-out ${pointIndex * 0.05}s forwards`
                  }}
                  title={`${product}: Price $${point.originalX.toFixed(2)}, Demand: ${point.originalY.toFixed(2)}`}
                ></div>
              ))}
            </React.Fragment>
          ))}
        </div>
      </div>
      <style jsx>{`
        @keyframes appear {
          from { transform: scale(0); opacity: 0; }
          to { transform: scale(1); opacity: 0.7; }
        }
      `}</style>
    </div>
  );
};

export default PriceSensitivityHeatmap;
