import React from 'react';

type CustomerSegmentPieProps = {
  data?: {
    name: string;
    percentage: number;
    color?: string;
  }[];
};

const CustomerSegmentPie: React.FC<CustomerSegmentPieProps> = ({ data = [] }) => {
  const segments = data.length > 0 ? data : [
    { name: 'Price Sensitive', percentage: 35, color: '#FF6384' },
    { name: 'Quality Focused', percentage: 25, color: '#36A2EB' },
    { name: 'Brand Loyal', percentage: 20, color: '#FFCE56' },
    { name: 'Convenience Seekers', percentage: 15, color: '#4BC0C0' },
    { name: 'Luxury Buyers', percentage: 5, color: '#9966FF' }
  ];

  const total = segments.reduce((sum, segment) => sum + segment.percentage, 0);

  const normalizedSegments = segments.map(segment => ({
    ...segment,
    percentage: total !== 0 ? (segment.percentage / total) * 100 : 0
  }));

  return (
    <div className="h-full relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-r from-purple-50 to-pink-50 opacity-50 rounded-lg"></div>
      <div className="relative h-full flex">
        <div className="flex-1 flex items-center justify-center">
          <div className="relative w-4/5 h-4/5">
            <svg viewBox="0 0 100 100" className="w-full h-full">
              {normalizedSegments.map((segment, index) => {
                const percentage = segment.percentage;
                const angle = (percentage / 100) * 360;
                const startAngle = index > 0
                  ? normalizedSegments.slice(0, index).reduce((sum, s) => sum + (s.percentage / 100) * 360, 0)
                  : 0;
                const endAngle = startAngle + angle;
                const startRad = (startAngle - 90) * (Math.PI / 180);
                const endRad = (endAngle - 90) * (Math.PI / 180);
                const x1 = 50 + 40 * Math.cos(startRad);
                const y1 = 50 + 40 * Math.sin(startRad);
                const x2 = 50 + 40 * Math.cos(endRad);
                const y2 = 50 + 40 * Math.sin(endRad);
                const largeArcFlag = angle > 180 ? 1 : 0;
                const d = `M 50 50 L ${x1} ${y1} A 40 40 0 ${largeArcFlag} 1 ${x2} ${y2} Z`;

                return (
                  <path
                    key={index}
                    d={d}
                    fill={segment.color || '#' + Math.floor(Math.random() * 16777215).toString(16)}
                    stroke="#fff"
                    strokeWidth="1"
                    className="transition-all duration-500 ease-out"
                    style={{
                      transformOrigin: 'center',
                      animation: 'fadeIn 1s ease-out, scaleIn 1s ease-out',
                    }}
                  />
                );
              })}
            </svg>
          </div>
        </div>

        <div className="w-1/3 flex flex-col justify-center pr-4">
          <div className="space-y-2">
            {normalizedSegments.map((segment, index) => (
              <div key={index} className="flex items-center">
                <div
                  className="w-3 h-3 rounded-full mr-2"
                  style={{ backgroundColor: segment.color || '#' + Math.floor(Math.random() * 16777215).toString(16) }}
                ></div>
                <div className="flex-1 min-w-0">
                  <div className="text-xs font-medium truncate">{segment.name}</div>
                  <div className="text-xs text-gray-500">{segment.percentage.toFixed(1)}%</div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Replace <style jsx> with CRA-compatible <style> */}
      <style
        dangerouslySetInnerHTML={{
          __html: `
            @keyframes fadeIn {
              from { opacity: 0; }
              to { opacity: 1; }
            }
            @keyframes scaleIn {
              from { transform: scale(0.8); }
              to { transform: scale(1); }
            }
          `
        }}
      />
    </div>
  );
};

export default CustomerSegmentPie;
